mapper.select:
select a.apply_code,a.dept_id,a.AMORTIZE_COEFFICIENT,c.dept_name,c.dept_code
from ACC_AMORTIZE_DEPT a 
left join HOS_DEPT_DICT c on a.group_id = c.group_id and a.hos_id = c.hos_id and a.dept_id = c.dept_id and c.is_stop = 0

	a.group_id = #{group_id}
	and a.hos_id = #{hos_id}
	and a.copy_code = #{copy_code}
	and a.apply_code = #{apply_code}

		and a.dept_id = #{dept_id}


		and a.dept_name like '%'||#{dept_name}||'%'

mapper.insert:
insert into ACC_AMORTIZE_DEPT ( group_id, hos_id, copy_code, apply_code, dept_id, AMORTIZE_COEFFICIENT ) select t.* from(
select #{item.group_id,jdbcType=INTEGER}, #{item.hos_id,jdbcType=INTEGER}, #{item.copy_code,jdbcType=VARCHAR}, #{item.apply_code,jdbcType=VARCHAR}, #{item.dept_id,jdbcType=INTEGER}, #{item.amortize_coefficient,jdbcType=NUMERIC} from dual
)t

mapper.delete:
DELETE FROM ACC_AMORTIZE_DEPT WHERE

	apply_code = #{item.apply_code}
	and group_id = #{item.group_id}
	and hos_id = #{item.hos_id}
	and copy_code = #{item.copy_code}
	and dept_id = #{item.dept_id}

DELETE FROM ACC_AMORTIZE_DEPT WHERE

	apply_code = #{item.apply_code}
	and group_id = #{item.group_id}
	and hos_id = #{item.hos_id}
	and copy_code = #{item.copy_code}
