mapper.select:
SELECT 
a.proj_code,a.DEPT_ID,b.DEPT_NAME,a.FT_BL,a.NOTE,a.COOP_OBJ 
FROM ACC_COOP_PROJ_DETAIL a
LEFT JOIN HOS_DEPT_DICT b ON a.GROUP_ID = b.GROUP_ID AND a.HOS_ID = b.HOS_ID AND a.DEPT_ID = b.DEPT_ID AND b.IS_STOP = 0

	a.GROUP_ID = #{group_id}
	AND a.HOS_ID = #{hos_id}
	AND a.COPY_CODE = #{copy_code}
	AND a.coop_obj = 0

		AND a.PROJ_CODE = #{proj_code}

SELECT a.proj_code, a.FT_BL, a.NOTE, CASE a.COOP_OBJ WHEN 1 THEN b.CUS_NAME ELSE d.SUP_NAME END AS obj_name, CASE a.COOP_OBJ WHEN 1 THEN '客户' ELSE '供应商' END AS coop_obj_name, a.CUS_ID, a.SUP_ID, a.COOP_OBJ FROM ACC_COOP_PROJ_DETAIL a LEFT JOIN HOS_CUS_DICT b on a.GROUP_ID = b.GROUP_ID AND a.HOS_ID = b.HOS_ID AND a.CUS_ID = b.CUS_ID AND b.IS_STOP = 0 LEFT JOIN HOS_SUP_DICT d on a.GROUP_ID = d.GROUP_ID AND a.HOS_ID = d.HOS_ID AND a.SUP_ID = d.SUP_ID AND d.IS_STOP = 0
a.GROUP_ID = #{group_id} AND a.HOS_ID = #{hos_id} AND a.COPY_CODE = #{copy_code} AND a.coop_obj != 0
AND a.PROJ_CODE = #{proj_code}

SELECT 
	a.FT_BL,
	a.NOTE as dnote,
	CASE a.COOP_OBJ WHEN 0 THEN b.DEPT_NAME WHEN 1 THEN e.CUS_NAME ELSE d.SUP_NAME END AS coop_com,
	CASE a.COOP_OBJ WHEN 0 THEN '院内科室' WHEN 1 THEN '客户' WHEN 2 THEN '供应商' END AS coop_obj
FROM ACC_COOP_PROJ_DETAIL a
LEFT JOIN HOS_DEPT_DICT b ON a.GROUP_ID = b.GROUP_ID AND a.HOS_ID = b.HOS_ID AND a.DEPT_ID = b.DEPT_ID AND b.IS_STOP = 0
LEFT JOIN HOS_CUS_DICT e on a.GROUP_ID = e.GROUP_ID AND a.HOS_ID = e.HOS_ID AND a.CUS_ID = e.CUS_ID AND e.IS_STOP = 0
LEFT JOIN HOS_SUP_DICT d on a.GROUP_ID = d.GROUP_ID AND a.HOS_ID = d.HOS_ID AND a.SUP_ID = d.SUP_ID AND d.IS_STOP = 0

	a.GROUP_ID = #{group_id}
	AND a.HOS_ID = #{hos_id}
	AND a.COPY_CODE = #{copy_code}
	AND a.PROJ_CODE = #{proj_code}

mapper.insert:
insert into acc_coop_proj_detail( group_id, hos_id, copy_code, proj_code, coop_obj, dept_id, cus_id, sup_id, ft_bl, note ) select A.* from (
select #{item.group_id},#{item.hos_id},#{item.copy_code},#{item.proj_code}, #{item.coop_obj},#{item.dept_id},#{item.cus_id},#{item.sup_id},#{item.ft_bl},#{item.note} from dual
)A

mapper.delete:
delete from ACC_COOP_PROJ_DETAIL


		group_id = #{item.group_id}
		and
		hos_id = #{item.hos_id}
		and
		copy_code = #{item.copy_code}
		and
		proj_code = #{item.proj_code}

	delete from ACC_COOP_PROJ_DETAIL where
 	group_id = #{group_id}
	and hos_id = #{hos_id}
	and copy_code = #{copy_code}
	and proj_code = #{proj_code}
