mapper.select:
SELECT a.ser_num,a.create_date,a.proj_code,b.proj_name, a.coop_type,a.sm_my,a.user_id,c.user_name,a.state,a.note
FROM ACC_COOP_COST a
LEFT JOIN ACC_COOP_PROJ b ON a.PROJ_CODE = b.PROJ_CODE AND a.GROUP_ID = b.GROUP_ID AND a.HOS_ID = b.HOS_ID AND a.COPY_CODE = b.COPY_CODE AND b.IS_STOP = 0
LEFT JOIN SYS_USER c ON a.GROUP_ID = c.GROUP_ID AND a.HOS_ID = c.HOS_ID AND a.user_id = c.user_id and c.is_stop = 0

	a.group_id = #{group_id}
	and a.hos_id = #{hos_id}
	and a.copy_code = #{copy_code}

		and a.proj_code = #{proj_code}


		and a.create_date between to_date(#{year_month_beg},'yyyy-MM-dd') and to_date(#{year_month_end},'yyyy-MM-dd')


		and a.coop_type = #{coop_type}


		and a.user_id = #{user_id}


		and a.state = #{state}


order by a.ser_num,a.create_date

SELECT SER_NUM,to_char(CREATE_DATE,'yyyy-MM-dd') as CREATE_DATE,PROJ_CODE,COOP_TYPE,SM_MY,STATE,NOTE
FROM
	ACC_COOP_COST
where
	group_id = #{group_id}
	and hos_id = #{hos_id}
	and copy_code = #{copy_code}
	and ser_num = #{ser_num}

SELECT a.SER_NUM,to_char(a.CREATE_DATE,'yyyy-MM-dd') as CREATE_DATE,a.PROJ_CODE,b.proj_name,
decode(a.coop_type,1,'院内',2,'院外') as coop_type,a.SM_MY,
decode(a.state,1,'新建',2,'审核') as STATE,a.NOTE,c.user_name
FROM
	ACC_COOP_COST a
	LEFT JOIN SYS_USER c ON a.GROUP_ID = c.GROUP_ID AND a.HOS_ID = c.HOS_ID AND a.user_id = c.user_id and c.is_stop = 0
	LEFT JOIN ACC_COOP_PROJ b ON a.PROJ_CODE = b.PROJ_CODE AND a.GROUP_ID = b.GROUP_ID AND a.HOS_ID = b.HOS_ID AND a.COPY_CODE = b.COPY_CODE AND b.IS_STOP = 0
where
	a.group_id = #{group_id}
	and a.hos_id = #{hos_id}
	and a.copy_code = #{copy_code}
	and a.ser_num = #{ser_num}

SELECT a.ser_num,to_char(a.create_date,'yyyy-MM-dd') as create_date,a.proj_code,b.proj_name, 
decode(a.coop_type,1,'院内',2,'院外') as coop_type,
decode(a.state,1,'新建',2,'审核') as state,
a.sm_my,a.user_id,c.user_name,a.note
FROM ACC_COOP_COST a
LEFT JOIN ACC_COOP_PROJ b ON a.PROJ_CODE = b.PROJ_CODE AND a.GROUP_ID = b.GROUP_ID AND a.HOS_ID = b.HOS_ID AND a.COPY_CODE = b.COPY_CODE AND b.IS_STOP = 0
LEFT JOIN SYS_USER c ON a.GROUP_ID = c.GROUP_ID AND a.HOS_ID = c.HOS_ID AND a.user_id = c.user_id and c.is_stop = 0

	a.group_id = #{group_id}
	and a.hos_id = #{hos_id}
	and a.copy_code = #{copy_code}

		and a.proj_code = #{proj_code}


		and a.create_date between to_date(#{year_month_beg},'yyyy-MM-dd') and to_date(#{year_month_end},'yyyy-MM-dd')


		and a.coop_type = #{coop_type}


		and a.user_id = #{user_id}


		and a.state = #{state}


order by a.ser_num,a.create_date

select max(ser_num) 
from ACC_COOP_COST
where group_id = #{group_id}
and hos_id = #{hos_id}
and copy_code = #{copy_code}
and create_date between to_date(#{begin_date},'yyyy-MM-dd') and to_date(#{end_date},'yyyy-MM-dd')

mapper.update:
UPDATE ACC_COOP_COST


		CREATE_DATE = to_date(#{create_date},'yyyy-MM-dd'),


		STATE = #{state},


		SM_MY = #{sm_my},


		coop_type = #{coop_type},


		note = #{note},


WHERE group_id = #{group_id}
	and hos_id = #{hos_id}
	and copy_code = #{copy_code}
	and ser_num = #{ser_num}

mapper.select:
select distinct a.user_id as id,user_name as text from sys_user a inner join ACC_COOP_COST b on a.group_id=b.group_id and a.hos_id=b.hos_id and a.user_id=b.user_id
a.group_id = #{group_id} and a.hos_id = #{hos_id}
spell_code like '${key}%' or wbx_code like '${key}%' or user_code like '${key}%' or user_name like '%${key}%'

delete.foreach:
	delete ACC_COOP_COST where
 	group_id = #{item.group_id}
	and hos_id = #{item.hos_id}
	and copy_code = #{item.copy_code}
	and ser_num = #{item.ser_num}

mapper.delete:
	delete ACC_COOP_COST where
 	group_id = #{group_id}
	and hos_id = #{hos_id}
	and copy_code = #{copy_code}
	and ser_num = #{ser_num}

mapper.select:
SELECT PROJ_CODE as id,PROJ_NAME as text ,COOP_TYPE ,NOTE
FROM ACC_COOP_PROJ

	group_id = #{group_id}
	and hos_id = #{hos_id}
	and copy_code = #{copy_code}

		PROJ_CODE like '${key}%' or
		PROJ_NAME like '${key}%'

select count(1) from ACC_BUSI_LOG_0104
where group_id = #{group_id}
and hos_id  = #{hos_id}
and copy_code = #{copy_code}
and business_no in #{ser_num}

SELECT a.COOP_OBJ,decode(a.coop_obj,0,'内部科室',1,'客户',2,'供应商') as coop_obj_name,a.DEPT_ID, b.dept_no,a.SUP_ID,decode(d.sup_no,null,0,d.sup_no) as sup_no,a.CUS_ID, decode(e.cus_no,null,0,e.cus_no) as cus_no,a.FT_BL,a.NOTE ,b.dept_code||' '||b.dept_name as dept_name, decode(a.coop_obj,0,'内部科室',1,e.cus_code||' '||e.cus_name,2,d.sup_code||' '||d.sup_name) as obj_name FROM ACC_COOP_PROJ_DETAIL a LEFT JOIN HOS_DEPT_DICT b ON a.GROUP_ID = b.GROUP_ID AND a.HOS_ID = b.HOS_ID AND a.DEPT_ID = b.DEPT_ID AND b.IS_STOP = 0 LEFT JOIN HOS_CUS_DICT e on a.GROUP_ID = e.GROUP_ID AND a.HOS_ID = e.HOS_ID AND a.CUS_ID = e.CUS_ID AND e.IS_STOP = 0 LEFT JOIN HOS_SUP_DICT d on a.GROUP_ID = d.GROUP_ID AND a.HOS_ID = d.HOS_ID AND a.SUP_ID = d.SUP_ID AND d.IS_STOP = 0 where a.group_id = #{group_id} and a.hos_id = #{hos_id} and a.copy_code = #{copy_code} and a.PROJ_CODE = #{proj_code}
and a.coop_obj != 0
and a.coop_obj = 0
