mapper.update:

        UPDATE ACC_MATCH_INIT SET

			 bal_os = #{bal_os}
			 ,
			 sum_od = #{sum_od}
			 ,
			 sum_oc = #{sum_oc}
			 ,
			 end_os = #{end_os}

        WHERE 
		     group_id = #{group_id}
			 and hos_id = #{hos_id}
			 and copy_code = #{copy_code}
			 and acc_year = #{acc_year}
			 and proj_id = #{proj_id}
			 and proj_no = #{proj_no}

update.foreach:

        UPDATE ACC_MATCH_INIT SET
			 bal_os = #{bal_os}
			 ,
			 sum_od = #{sum_od}
			 ,
			 sum_oc = #{sum_oc}
			 ,
			 end_os = #{end_os}

        WHERE 
		     group_id = #{group_id}
			 and hos_id = #{hos_id}
			 and copy_code = #{copy_code}
			 and acc_year = #{acc_year}
			 and proj_id = #{proj_id}

mapper.delete:

        DELETE FROM ACC_MATCH_INIT
        WHERE 
		     group_id = #{group_id}
			 and hos_id = #{hos_id}
			 and copy_code = #{copy_code}
			 and acc_year = #{acc_year}
			 and proj_id = #{proj_id}

        DELETE FROM ACC_MATCH_INIT WHERE 

		 group_id = #{item.group_id}
			 and hos_id = #{item.hos_id}
			 and copy_code = #{item.copy_code}
			 and acc_year = #{item.acc_year}
			 and proj_id = #{item.proj_id}

mapper.select:

        SELECT 
              ami.group_id
			 ,   ami.hos_id
			 ,    ami.copy_code
			 ,   ami.acc_year
			 ,    ami.proj_id
			 ,    ami.proj_no
			 	,hpd.proj_code
			 	,hpd.proj_name
			 	,he.emp_name
             ,     ami.bal_os
             ,ami.sum_od,
             ami.sum_oc,
			 ami.end_os
        FROM ACC_MATCH_INIT ami
        left join HOS_PROJ_DICT hpd on
        ami.group_id = hpd.group_id 
        and ami.hos_id = hpd.hos_id
        and ami.proj_id = hpd.proj_id
        and hpd.is_stop = 0
        left join ACC_PROJ_ATTR apa on
        ami.group_id = apa.group_id 
        and ami.hos_id = apa.hos_id
        and ami.proj_id = apa.proj_id
     	left join HOS_EMP he on 
     	apa.group_id = he.group_id 
        and apa.hos_id = he.hos_id   
        and apa.con_emp_id = he.emp_id
      left join hos_proj_type hp on
      hpd.group_id = hp.group_id
      and hpd.hos_id = hp.hos_id
      and hpd.type_code = hp.type_code


            		AND hp.attr_code = #{attr_code,jdbcType=VARCHAR}


                    AND ami.group_id = #{group_id}


                    AND ami.hos_id = #{hos_id}


                    AND ami.copy_code = #{copy_code}


                    AND ami.acc_year = #{acc_year}


                    AND ami.proj_id = #{proj_id}


                    AND ami.proj_no = #{proj_no}


                    AND ami.bal_os = #{bal_os}


                    AND ami.sum_od = #{sum_od}


                    AND ami.sum_oc = #{sum_oc}


                    AND ami.end_os = #{end_os}


                    AND hpd.type_code = #{type_code}


                    AND apa.level_code = #{level_code}


                    AND apa.dept_id = #{dept_id}


                    AND apa.con_emp_id = #{con_emp_id}


				order by ami.proj_id asc


        SELECT 
              ami.group_id
			 ,   ami.hos_id
			 ,    ami.copy_code
			 ,   ami.acc_year
			 ,    ami.proj_id
			 ,    ami.proj_no
			 	,hpd.proj_code
			 	,hpd.proj_name
			 	,he.emp_name
             ,     ami.bal_os
             ,ami.sum_od,
             ami.sum_oc,
			 ami.end_os
        FROM ACC_MATCH_INIT ami
        left join HOS_PROJ_DICT hpd on
        ami.group_id = hpd.group_id 
        and ami.hos_id = hpd.hos_id
        and ami.proj_id = hpd.proj_id
        and hpd.is_stop = 0
        left join ACC_PROJ_ATTR apa on
        ami.group_id = apa.group_id 
        and ami.hos_id = apa.hos_id
        and ami.proj_id = apa.proj_id
     	left join HOS_EMP he on 
     	apa.group_id = he.group_id 
        and apa.hos_id = he.hos_id   
        and apa.con_emp_id = he.emp_id


                    AND ami.group_id = #{group_id}


                    AND ami.hos_id = #{hos_id}


                    AND ami.copy_code = #{copy_code}


                    AND ami.acc_year = #{acc_year}


                    AND ami.proj_id = #{proj_id}


                    AND ami.proj_no = #{proj_no}


                    AND ami.bal_os = #{bal_os}


                    AND ami.sum_od = #{sum_od}


                    AND ami.sum_oc = #{sum_oc}


                    AND ami.end_os = #{end_os}


                    AND hpd.type_code = #{type_code}


                    AND apa.level_code = #{level_code}


                    AND apa.dept_id = #{dept_id}


                    AND apa.con_emp_id = #{con_emp_id}


				order by ami.proj_id asc
