mapper.select:

		SELECT 
		 rad.group_id as group_id, rad.hos_id, rad.copy_code, rad.apply_no, rad.ass_card_no
  , rad.price,  rad.add_depre, rad.add_manage_depre
  , rad.cur_money, rad.fore_money, rad.new_price,rad.note,
  ad.ass_name as ass_name,ad.ass_code as ass_code
from ASS_REMOULD_A_DETAIL rad
left join ass_card_special acs on acs.ass_card_no=rad.ass_card_no
left join ass_dict ad on ad.ass_id=acs.ass_id


		AND rad.group_id = #{group_id,jdbcType=INTEGER}


		AND rad.hos_id = #{hos_id,jdbcType=INTEGER}


		AND rad.copy_code = #{copy_code,jdbcType=VARCHAR}


		AND rad.apply_no  in 

                #{item,jdbcType=VARCHAR}   



		AND rad.ass_card_no = #{ass_card_no,jdbcType=VARCHAR}


		AND rad.price = #{price,jdbcType=INTEGER}



		AND rad.add_depre = #{add_depre,jdbcType=INTEGER}


		AND rad.add_manage_depre = #{add_manage_depre,jdbcType=INTEGER}


		AND rad.cur_money = #{cur_money,jdbcType=INTEGER}


		AND rad.fore_money = #{fore_money,jdbcType=INTEGER}


		AND rad.new_price = #{new_price,jdbcType=INTEGER}


		AND rad.note = #{note,jdbcType=VARCHAR}


		order by rad.group_id asc

SELECT a.group_id, a.hos_id, a.copy_code, a.apply_no, a.ass_card_no, c.ass_code, c.ass_name, a.price,
a.add_depre, a.add_manage_depre, a.cur_money, a.fore_money, a.new_price, a.note FROM ass_remould_a_detail a left join ass_card_special b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_card_no = b.ass_card_no left join ass_no_dict c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_id = c.ass_id and c.is_stop = 0 WHERE a.group_id = #{group_id,jdbcType=INTEGER} and a.hos_id = #{hos_id,jdbcType=INTEGER} and a.copy_code = #{copy_code,jdbcType=VARCHAR} and a.apply_no = #{apply_no,jdbcType=VARCHAR}
and a.ass_card_no = #{ass_card_no,jdbcType=VARCHAR}


SELECT 
group_id,
hos_id,
copy_code,
apply_no,
ass_card_no,
price,

add_depre,
add_manage_depre,
cur_money,
fore_money,
new_price,
note
FROM ASS_REMOULD_A_DETAIL 


AND group_id = #{group_id,jdbcType=INTEGER}


AND hos_id = #{hos_id,jdbcType=INTEGER}


AND copy_code = #{copy_code,jdbcType=VARCHAR}


AND apply_no = #{apply_no,jdbcType=VARCHAR}


AND ass_card_no = #{ass_card_no,jdbcType=VARCHAR}


AND price = #{price,jdbcType=INTEGER}



AND add_depre = #{add_depre,jdbcType=INTEGER}


AND add_manage_depre = #{add_manage_depre,jdbcType=INTEGER}


