select * from (SELECT a.group_id, a.hos_id, a.copy_code, a.bill_no, a.invoice_no, a.bill_date, a.ven_id, a.ven_no, b.sup_code ven_code, b.sup_name ven_name, a.pact_code, a.bill_money, a.state, case when a.state = 0 then '新建' when a.state = 1 then '审核' when a.state = 2 then '确认' else '' end state_name, a.create_emp, su1.user_name create_emp_name, a.create_date, a.audit_emp, su2.user_name audit_emp_name, a.audit_date, a.note, a.store_id, a.store_no, hsd.store_code, hsd.store_name FROM ASS_BILL_MAIN a left join hos_sup_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.ven_id = b.sup_id and b.is_stop = 0
left join hos_store_dict hsd on a.group_id = hsd.group_id and a.hos_id = hsd.hos_id and a.store_id = hsd.store_id and hsd.is_stop = 0
left join sys_user su1 on a.group_id = su1.group_id and a.hos_id = su1.hos_id and a.create_emp = su1.user_id
left join sys_user su2 on a.group_id = su2.group_id and a.hos_id = su2.hos_id and a.audit_emp = su2.user_id
AND a.group_id = #{group_id}
AND a.hos_id = #{hos_id}
AND a.copy_code = #{copy_code}
AND a.bill_no = #{bill_no}
AND a.invoice_no = #{invoice_no}
AND a.bill_date between to_date(#{bill_date_beg},'YYYY-MM-DD') and to_date(#{bill_date_end},'YYYY-MM-DD')
AND a.ven_id = #{ven_id}
AND a.pact_code = #{pact_code}
AND a.bill_money = #{bill_money}
AND a.state = #{state}
AND a.create_emp = #{create_emp}
AND a.create_date between to_date(#{create_date_beg},'YYYY-MM-DD') and to_date(#{create_date_end},'YYYY-MM-DD')
AND a.audit_emp = #{audit_emp}
AND a.audit_date between to_date(#{audit_date_beg},'YYYY-MM-DD') and to_date(#{audit_date_end},'YYYY-MM-DD')
AND a.note = #{note}
AND a.store_id = #{store_id}
and exists ( select 1 from ass_bill_detail ab where ab.group_id = a.group_id and ab.hos_id = a.hos_id and ab.copy_code = a.copy_code and ab.bill_no = a.bill_no and ab.naturs_code = #{ass_nature}
