AND a.create_date between to_date(#{create_date_beg,jdbcType=DATE},'YYYY-MM-DD') and to_date(#{create_date_end,jdbcType=DATE},'YYYY-MM-DD')
AND a.audit_emp = #{audit_emp,jdbcType=VARCHAR}
AND a.audit_date between to_date(#{audit_date_beg,jdbcType=DATE},'YYYY-MM-DD') and to_date(#{audit_date_end,jdbcType=DATE},'YYYY-MM-DD')
AND a.note = #{note,jdbcType=VARCHAR}
) t order by t.bill_no desc

select distinct a.group_id, a.hos_id, a.copy_code, a.ass_in_no, a.store_id, a.store_no, b.store_code, b.store_name, a.ven_id, a.ven_no, c.sup_code as ven_code, c.sup_name as ven_name, a.purc_emp, su1.emp_name as purc_emp_name, a.dept_id, a.dept_no, d.dept_code, d.dept_name, a.in_money, a.note, a.create_emp, su2.user_name as create_emp_name, a.create_date, a.in_date, a.confirm_emp, su3.user_name as confirm_emp_name, a.invoice_no, a.state, case when a.state = 0 then '新建' when a.state = 1 then '审核' when a.state = 2 then '入库确认' else null end as state_name from v_ass_in_main a left join hos_store_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.store_id = b.store_id and b.is_stop = 0 left join hos_sup_dict c on a.group_id = c.group_id and a.hos_id = c.hos_id and a.ven_id = c.sup_id and c.is_stop = 0 left join hos_dept_dict d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.dept_id = d.dept_id and d.is_stop = 0 left join hos_proj_dict e on a.group_id = e.group_id and a.hos_id = e.hos_id and a.proj_id = e.proj_id and e.is_stop = 0 left join hos_emp_dict su1 on a.group_id = su1.group_id and a.hos_id = su1.hos_id and a.purc_emp = su1.emp_id and su1.is_stop = 0 left join sys_user su2 on a.group_id = su2.group_id and a.hos_id = su2.hos_id and a.create_emp = su2.user_id left join sys_user su3 on a.group_id = su3.group_id and a.hos_id = su3.hos_id and a.confirm_emp = su3.user_id left join ass_usage_dict aud on a.group_id = aud.group_id and a.hos_id = aud.hos_id and a.copy_code = aud.copy_code and a.ass_purpose = aud.equi_usage_code left join v_ass_card t1 on a.group_id = t1.group_id and a.hos_id = t1.hos_id and a.copy_code = t1.copy_code and a.ass_in_no = t1.ASS_IN_NO and a.ass_naturs = t1.ass_naturs left join v_ass_pay_stage t on t.group_id = t1.group_id and t.hos_id = t1.hos_id and t.copy_code = t1.copy_code and t.ass_card_no = t1.ass_card_no and t.ass_naturs = t1.ass_naturs left join v_ass_bill_vis vabv on t.group_id = vabv.group_id and t.hos_id = vabv.hos_id and t.copy_code = vabv.copy_code and t.ass_card_no = vabv.ass_card_no
where a.group_id = #{group_id} and a.hos_id = #{hos_id} and a.copy_code = #{copy_code} and a.ass_in_no is not null and a.invoice_no is not null
and a.ven_id = #{ven_id}
and a.store_id = #{store_id}
and a.ass_in_no = #{ass_in_no}
and a.invoice_no = #{invoice_no}
and nvl(t1.dispose_type,0) != 41
AND a.in_date >= to_date(#{in_date_beg},'YYYY-MM-DD')
AND a.in_date <= to_date(#{in_date_end},'YYYY-MM-DD')
AND a.create_date >= to_date(#{begin_date_beg},'YYYY-MM-DD')
AND a.create_date <= to_date(#{end_date_beg},'YYYY-MM-DD')
and t.unpay_money > 0 and t.unbill_money > 0 and t.pay_plan_money > nvl(vabv.bill_money,0) and t1.use_state != 0 and exists (select 1 from v_user_data_perm b where a.group_id=b.group_id and a.hos_id=b.hos_id and to_char(a.store_id)=b.perm_code AND TABLE_CODE = upper('hos_store_dict') and b.user_id=#{user_id,jdbcType=INTEGER} and (b.is_read = 1 or b.is_write = 1 )) order by a.ass_in_no desc
