mapper.insert:
INSERT INTO ASS_Change_DETAIL_House ( group_id , hos_id , copy_code , change_no , ass_card_no , old_price , change_price , price , note
) select t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.change_no} , #{item.ass_card_no} , nvl(#{item.old_price},0) , nvl(#{item.change_price},0) , nvl(#{item.price},0) , nvl(#{item.note},'')
from dual
)t

mapper.update:

UPDATE ass_change_detail_House 


old_price = #{old_price}
,


change_price = #{change_price}
,


price = #{price}
,


note = #{note}
,




AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND change_no = #{change_no}


AND ass_card_no = #{ass_card_no}


UPDATE ass_change_detail_House  set
	change_price = nvl(#{change_price},0)
	,
	price =  nvl(#{price},0)

AND group_id = #{group_id}
AND hos_id = #{hos_id}
AND copy_code = #{copy_code}
AND change_no = #{change_no}
AND ass_card_no = #{ass_card_no}

update.foreach:
UPDATE ass_change_detail_House set
change_price = nvl(#{item.change_price},0)
,
price = nvl(#{item.price},0)

group_id = #{item.group_id}
AND hos_id = #{item.hos_id}
AND copy_code = #{item.copy_code}
AND change_no = #{item.change_no}
AND ass_card_no = #{item.ass_card_no}

UPDATE ass_change_detail_House 


old_price = #{item.old_price}
,


change_price = #{item.change_price}
,


price = #{item.price}
,


note = #{item.note}
,




group_id = #{item.group_id}


AND hos_id = #{item.hos_id}


AND copy_code = #{item.copy_code}


AND change_no = #{item.change_no}


AND ass_card_no = #{item.ass_card_no}

mapper.delete:

DELETE FROM ass_change_detail_House 


group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND change_no = #{change_no}


AND ass_card_no = #{ass_card_no}

DELETE FROM ass_change_detail_House 



		group_id = #{item.group_id}


		AND hos_id = #{item.hos_id}


		AND copy_code = #{item.copy_code}


		AND change_no = #{item.change_no}


		AND ass_card_no = #{item.ass_card_no}

mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.change_no, a.ass_card_no, c.ass_id, c.ass_no, c.ass_code, c.ass_name, a.old_price, a.change_price, a.price, a.note FROM ass_change_detail_House a left join ass_card_House b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_card_no = b.ass_card_no
left join ass_no_dict c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_id = c.ass_id and c.is_stop = 0
AND a.group_id = #{group_id}
AND a.hos_id = #{hos_id}
AND a.copy_code = #{copy_code}
AND a.change_no = #{change_no}
AND a.ass_card_no = #{ass_card_no}
AND a.old_price = #{old_price}
AND a.change_price = #{change_price}
AND a.price = #{price}
AND a.note = #{note}
order by a.ass_card_no asc

SELECT a.group_id, a.hos_id, a.copy_code, a.change_no, a.ass_card_no, c.ass_id, c.ass_no, c.ass_code, c.ass_name, a.old_price, a.change_price, a.price, a.note FROM ass_change_detail_House a left join ass_card_House b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_card_no = b.ass_card_no
left join ass_no_dict c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_id = c.ass_id and c.is_stop = 0
AND a.group_id = #{group_id} AND a.hos_id = #{hos_id} AND a.copy_code = #{copy_code} AND a.change_no = #{change_no}

SELECT a.group_id, a.hos_id, a.copy_code, a.change_no, a.ass_card_no, c.ass_id, c.ass_no, c.ass_code, c.ass_name, a.old_price, a.change_price, a.price, a.note FROM ass_change_detail_House a left join ass_card_House b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_card_no = b.ass_card_no
left join ass_no_dict c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_id = c.ass_id and c.is_stop = 0
AND a.group_id = #{group_id} AND a.hos_id = #{hos_id} AND a.copy_code = #{copy_code} AND a.change_no = #{change_no}


SELECT 
group_id,
hos_id,
copy_code,
change_no,
ass_card_no,
old_price,
change_price,
price,
note
FROM ASS_Change_DETAIL_House 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND change_no = #{change_no}


AND ass_card_no = #{ass_card_no}


AND old_price = #{old_price}


AND change_price = #{change_price}


AND price = #{price}


AND note = #{note}


order by group_id asc


