mapper.insert:
INSERT INTO ASS_Change_LAND ( group_id , hos_id , copy_code , change_no , ven_id , ven_no , create_emp , create_date , state , note
) select t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.change_no} , #{item.ven_id} , #{item.ven_no} , #{item.create_emp} , #{item.create_date} , #{item.state} , #{item.note}
from dual
)t

mapper.update:

UPDATE ass_change_land 


ven_id = #{ven_id}
,


ven_no = #{ven_no}
,


create_date = to_date(#{create_date},'YYYY-MM-DD')
,


note = #{note}
,


note = null
,




AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND change_no = #{change_no}

update.foreach:
