mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.change_no, a.ven_id, a.ven_no, c.sup_code ven_code, c.sup_name ven_name, a.create_emp, su1.user_name create_emp_name, a.create_date, a.audit_emp, su2.user_name audit_emp_name, a.change_date, a.state, case when a.state = 0 then '新建' when a.state = 1 then '审核' when a.state = 2 then '确认' else '' end state_name, a.note FROM ASS_CHANGE_LAND a
left join hos_sup_dict c on a.group_id = c.group_id and a.hos_id = c.hos_id and a.ven_id = c.sup_id and c.is_stop = 0
left join sys_user su1 on a.group_id = su1.group_id and a.hos_id = su1.hos_id and a.create_emp = su1.user_id
left join sys_user su2 on a.group_id = su2.group_id and a.hos_id = su2.hos_id and a.audit_emp = su2.user_id
AND a.group_id = #{group_id}
AND a.hos_id = #{hos_id}
AND a.copy_code = #{copy_code}
AND a.change_no = #{change_no}
AND a.ven_id = #{ven_id}
AND a.ven_no = #{ven_no}
AND a.create_emp = #{create_emp}
AND a.create_date between to_date(#{create_date_beg},'YYYY-MM-DD') and to_date(#{create_date_end},'YYYY-MM-DD')
AND a.audit_emp = #{audit_emp}
AND a.change_date between to_date(#{change_date_beg},'YYYY-MM-DD') and to_date(#{change_date_end},'YYYY-MM-DD')
AND a.state = #{state}
AND a.note = #{note}
order by a.create_date desc


SELECT 
group_id,
hos_id,
copy_code,
change_no,
ven_id,
ven_no,
create_emp,
create_date,
audit_emp,
change_date,
state,
note
FROM ASS_Change_LAND 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND change_no = #{change_no}


AND ven_id = #{ven_id}


AND ven_no = #{ven_no}


AND create_emp = #{create_emp}


AND create_date = #{create_date}


AND audit_emp = #{audit_emp}


AND change_date = #{change_date}


AND state = #{state}


AND note = #{note}


order by create_date desc


SELECT 
group_id,
hos_id,
copy_code,
change_no,
ven_id,
ven_no,
create_emp,
create_date,
audit_emp,
change_date,
state,
note
FROM ASS_Change_LAND 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND change_no = #{change_no}


AND ven_id = #{ven_id}


AND ven_no = #{ven_no}


AND create_emp = #{create_emp}


AND create_date = #{create_date}


AND audit_emp = #{audit_emp}


AND change_date = #{change_date}


AND state = #{state}


AND note = #{note}


order by create_date desc

SELECT change_no  FROM ASS_Change_LAND 
WHERE
	group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}

	AND change_no in  (${change_no})



	and state != #{state,jdbcType=INTEGER}
