mapper.insert:
INSERT INTO ASS_Change_SOURCE_Other ( group_id , hos_id , copy_code , change_no , ass_card_no , source_id , old_price , change_price , price , note
) select t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.change_no} , #{item.ass_card_no} , #{item.source_id} , nvl(#{item.old_price},0) , nvl(#{item.change_price},0) , nvl(#{item.price},0) , nvl(#{item.note},'')
from dual
)t

mapper.update:

UPDATE ass_change_source_Other 


old_price = #{old_price}
,


change_price = #{change_price}
,


price = #{price}
,


note = #{note}
,




AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND change_no = #{change_no}


AND ass_card_no = #{ass_card_no}


AND source_id = #{source_id}

update.foreach:
UPDATE ass_change_source_Other 


old_price = #{item.old_price}
,


change_price = #{item.change_price}
,


price = #{item.price}
,


note = #{item.note}
,




AND group_id = #{item.group_id}


AND hos_id = #{item.hos_id}


AND copy_code = #{item.copy_code}


AND change_no = #{item.change_no}


AND ass_card_no = #{item.ass_card_no}


AND source_id = #{item.source_id}

mapper.delete:

DELETE FROM ass_change_source_Other 


