mapper.select:

SELECT 
	a.group_id,
	a.hos_id,
	a.copy_code,
	a.change_no,
	a.ass_card_no,
	a.source_id,
	b.source_code||' '||b.source_name source_name,
	a.old_price,
	a.change_price,
	a.price,
	a.note
FROM ASS_Change_SOURCE_Other a
left join hos_source b on
a.group_id = b.group_id
and a.hos_id = b.hos_id
and a.source_id = b.source_id


	AND a.group_id = #{group_id}


	AND a.hos_id = #{hos_id}


	AND a.copy_code = #{copy_code}


	AND a.change_no = #{change_no}


	AND a.ass_card_no = #{ass_card_no}


	AND a.source_id = #{source_id}


	AND a.old_price = #{old_price}


	AND a.change_price = #{change_price}


	AND a.price = #{price}


	AND a.note = #{note}


order by a.ass_card_no,a.source_id asc


SELECT 
group_id,
hos_id,
copy_code,
change_no,
ass_card_no,
source_id,
old_price,
change_price,
price,
note
FROM ASS_Change_SOURCE_Other 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND change_no = #{change_no}


AND ass_card_no = #{ass_card_no}


AND source_id = #{source_id}


AND old_price = #{old_price}


AND change_price = #{change_price}


AND price = #{price}


AND note = #{note}


order by group_id asc


SELECT 
group_id,
hos_id,
copy_code,
change_no,
ass_card_no,
source_id,
old_price,
