mapper.insert:
INSERT INTO ASS_CHECK_DP_DETAIL_SPECIAL ( group_id , hos_id , copy_code , check_plan_no , check_no , dept_id , dept_no , ass_id , ass_no , ass_card_no , acc_amount , price , add_depre , add_depre_month , cur_money , fore_money , p_reason
) select t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.check_plan_no} , #{item.check_no} , #{item.dept_id} , #{item.dept_no} , #{item.ass_id} , #{item.ass_no} , nvl(#{item.ass_card_no},'') ass_card_no , nvl(#{item.acc_amount},0) acc_amount , nvl(#{item.price},0) price , nvl(#{item.add_depre},0) add_depre , nvl(#{item.add_depre_month},0) add_depre_month , nvl(#{item.cur_money},0) cur_money , nvl(#{item.fore_money},0) fore_money , nvl(#{item.p_reason},'') p_reason
from dual
)t

mapper.update:

UPDATE ass_check_dp_detail_special 


ass_card_no = #{ass_card_no}
,


acc_amount = #{acc_amount}
,


price = #{price}
,


add_depre = #{add_depre}
,


add_depre_month = #{add_depre_month}
,


cur_money = #{cur_money}
,


fore_money = #{fore_money}
,


p_reason = #{p_reason}
,


where                 
AND group_id = #{group_id}
AND hos_id = #{hos_id}
AND copy_code = #{copy_code}
AND check_plan_no = #{check_plan_no}
AND check_no = #{check_no}

AND dept_id = #{dept_id}


AND dept_no = #{dept_no}


AND ass_id = #{ass_id}


AND ass_no = #{ass_no}


AND ass_card_no = #{ass_card_no}

