mapper.select:
		SELECT 
		    a.ass_card_no,a.acc_amount,a.price,a.add_depre_month,a.cur_money,a.fore_money,ad.ass_name,ad.ass_id||'@'||ad.ass_no ass_id  ,ad.ass_code
			from ASS_CHECK_DP_DETAIL_SPECIAL a left join ASS_NO_DICT ad
			            on ad.group_id = a.group_id
						and ad.hos_id = a.hos_id
						and ad.copy_code = a.copy_code
						and ad.ass_id=a.ass_id
						and ad.is_stop = 0
	    where                 
	    a.group_id = #{group_id}
	    AND a.hos_id = #{hos_id}
	    AND a.copy_code = #{copy_code}
	    	and a.check_plan_no=#{check_plan_no_s}
			and a.check_no=#{check_no}
   	 order by a.ass_card_no asc

	select * from (	
		SELECT 
		    a.ass_card_no,a.acc_amount,a.price,a.add_depre_month,a.cur_money,a.fore_money,ad.ass_name,ad.ass_id||'@'||ad.ass_no ass_id  ,ad.ass_code
			from ASS_CHECK_DP_DETAIL_SPECIAL a left join ASS_NO_DICT ad
			            on ad.group_id = a.group_id
						and ad.hos_id = a.hos_id
						and ad.copy_code = a.copy_code
						and ad.ass_id=a.ass_id
						and ad.is_stop = 0
	    where                 
	    a.group_id = #{group_id}
	    AND a.hos_id = #{hos_id}
	    AND a.copy_code = #{copy_code}
	    	and a.check_plan_no=#{check_plan_no_s}
			and a.check_no=#{check_no}

		union all

		SELECT 
		    a.ass_card_no,a.acc_amount,a.price,a.add_depre_month,a.cur_money,a.fore_money,ad.ass_name,ad.ass_id||'@'||ad.ass_no ass_id  ,ad.ass_code
			from ASS_CHECK_DP_DETAIL_General a left join ASS_NO_DICT ad
			            on ad.group_id = a.group_id
						and ad.hos_id = a.hos_id
						and ad.copy_code = a.copy_code
						and ad.ass_id=a.ass_id
						and ad.is_stop = 0
	    where                 
	    a.group_id = #{group_id}
	    AND a.hos_id = #{hos_id}
	    AND a.copy_code = #{copy_code}
	    	and a.check_plan_no=#{check_plan_no_g}
			and a.check_no=#{check_no}	

		union all

		SELECT 
		    a.ass_card_no,a.acc_amount,a.price,a.add_depre_month,a.cur_money,a.fore_money,ad.ass_name,ad.ass_id||'@'||ad.ass_no ass_id  ,ad.ass_code
			from ASS_CHECK_DP_DETAIL_other a left join ASS_NO_DICT ad
			            on ad.group_id = a.group_id
						and ad.hos_id = a.hos_id
						and ad.copy_code = a.copy_code
						and ad.ass_id=a.ass_id
						and ad.is_stop = 0
	    where                 
	    a.group_id = #{group_id}
	    AND a.hos_id = #{hos_id}
	    AND a.copy_code = #{copy_code}
	    	and a.check_plan_no=#{check_plan_no_o}
			and a.check_no=#{check_no}	
    ) t
    order by t.ass_card_no asc

SELECT group_id, hos_id, copy_code, check_plan_no, check_no, dept_id, dept_no, ass_id, ass_no, ass_card_no, acc_amount, price, add_depre, add_depre_month, cur_money, fore_money, p_reason FROM ASS_CHECK_DP_DETAIL_SPECIAL WHERE group_id = #{group_id} and hos_id = #{hos_id} and copy_code = #{copy_code} and check_plan_no = #{check_plan_no} and check_no = #{check_no} and dept_id = #{dept_id} and dept_no = #{dept_no} and ass_id = #{ass_id} and ass_no = #{ass_no}
AND ass_card_no = #{ass_card_no}
order by ass_id asc

SELECT group_id, hos_id, copy_code, check_plan_no, check_no, dept_id, dept_no, ass_id, ass_no, ass_card_no, acc_amount, price, add_depre, add_depre_month, cur_money, fore_money, p_reason FROM ASS_CHECK_DP_DETAIL_SPECIAL group_id = #{group_id} and hos_id = #{hos_id} and copy_code = #{copy_code} and check_plan_no = #{check_plan_no} and check_no = #{check_no} and dept_id = #{dept_id} and dept_no = #{dept_no} and ass_id = #{ass_id} and ass_no = #{ass_no}
AND ass_card_no = #{ass_card_no}
order by ass_id asc
