DELETE FROM ass_contract_detail WHERE


	group_id = #{item.group_id,jdbcType=INTEGER}


	and hos_id = #{item.hos_id,jdbcType=INTEGER}


	and copy_code =#{item.copy_code,jdbcType=VARCHAR}


		and contract_id = #{item.contract_id,jdbcType=INTEGER}


		and contract_detail_id = #{item.contract_detail_id,jdbcType=INTEGER}

mapper.select:

		SELECT distinct
    a.group_id,
    a.hos_id,
    a.copy_code,
    a.contract_id,
    a.contract_detail_id,
    a.contract_no,
    a.ass_id,
    a.ass_no,
    an.ass_code,
    an.ass_name,
    a.ass_spec,
    a.ass_model,
    a.ass_brand,
    a.fac_id,
    a.fac_no,
    h.fac_code,
    h.fac_name,
    a.contract_amount,
    a.contract_price,
    a.send_date,
    a.keep_repair_times,
    a.times_unit,
    a.is_fix,
    a.is_accept,
    a.is_bid,
    a.note
    FROM ASS_CONTRACT_DETAIL a
    left join ass_no_dict an on a.group_id=an.group_id and a.hos_id=an.hos_id and a.ass_id=an.ass_id and an.is_stop=0
    left join hos_fac h on a.group_id=h.group_id and a.hos_id=h.hos_id and a.fac_id=h.fac_id


		AND a.group_id = #{group_id,jdbcType=INTEGER}


		AND a.hos_id = #{hos_id,jdbcType=INTEGER}


		AND a.copy_code = #{copy_code,jdbcType=VARCHAR}


		AND a.contract_id = #{contract_id,jdbcType=INTEGER}


		AND a.contract_detail_id = #{contract_detail_id,jdbcType=INTEGER}


		AND a.contract_no = #{contract_no,jdbcType=VARCHAR}


		AND a.ass_id = #{ass_id,jdbcType=INTEGER}


		AND a.ass_no = #{ass_no,jdbcType=INTEGER}


		AND a.ass_spec = #{ass_spec,jdbcType=VARCHAR}


		AND a.ass_model = #{ass_model,jdbcType=VARCHAR}


		AND a.ass_brand = #{ass_brand,jdbcType=VARCHAR}


		AND a.fac_id = #{fac_id,jdbcType=INTEGER}


		AND a.fac_no = #{fac_no,jdbcType=INTEGER}


		AND a.contract_amount = #{contract_amount,jdbcType=INTEGER}


		AND a.contract_price = #{contract_price,jdbcType=INTEGER}


		AND a.send_date = #{send_date,jdbcType=DATE}


		AND a.keep_repair_times = #{keep_repair_times,jdbcType=INTEGER}


		AND a.times_unit = #{times_unit,jdbcType=INTEGER}


		AND a.is_fix = #{is_fix,jdbcType=INTEGER}


		AND a.is_accept = #{is_accept,jdbcType=INTEGER}


		AND a.is_bid = #{is_bid,jdbcType=INTEGER}


		AND a.note = #{note,jdbcType=VARCHAR}


		order by group_id asc


		SELECT 
		    a.group_id,
		    a.hos_id,
		    a.copy_code,
		    a.ass_id,
		    a.ass_no,
		    a.ass_spec,
		    a.ass_model,
		    a.ass_brand,
		    a.fac_id,
		    a.fac_no,
		    sum(a.contract_amount) contract_amount,
		    sum(a.contract_price) contract_price,
		    atd.ass_naturs ass_nature
    FROM ASS_CONTRACT_DETAIL a
    left join ass_no_dict an on a.group_id=an.group_id and a.hos_id=an.hos_id and a.ass_id=an.ass_id and an.is_stop=0
    left join ASS_TYPE_DICT atd on
			 an.copy_code = atd.copy_code
			and an.hos_id = atd.hos_id
			and an.group_id = atd.group_id
			and an.ass_type_id = atd.ass_type_id
    left join hos_fac h on a.group_id=h.group_id and a.hos_id=h.hos_id and a.fac_id=h.fac_id


		AND a.group_id = #{group_id,jdbcType=INTEGER}


		AND a.hos_id = #{hos_id,jdbcType=INTEGER}


		AND a.copy_code = #{copy_code,jdbcType=VARCHAR}


		AND a.contract_id = #{contract_id,jdbcType=INTEGER}


		AND a.contract_detail_id = #{contract_detail_id,jdbcType=INTEGER}


		AND a.contract_no = #{contract_no,jdbcType=VARCHAR}


		AND a.ass_id = #{ass_id,jdbcType=INTEGER}


		AND a.ass_no = #{ass_no,jdbcType=INTEGER}


		AND a.ass_spec = #{ass_spec,jdbcType=VARCHAR}


		AND a.ass_model = #{ass_model,jdbcType=VARCHAR}


		AND a.ass_brand = #{ass_brand,jdbcType=VARCHAR}


		AND a.fac_id = #{fac_id,jdbcType=INTEGER}


		AND a.fac_no = #{fac_no,jdbcType=INTEGER}


		AND a.contract_amount = #{contract_amount,jdbcType=INTEGER}


		AND a.contract_price = #{contract_price,jdbcType=INTEGER}


		AND a.send_date = #{send_date,jdbcType=DATE}


		AND a.keep_repair_times = #{keep_repair_times,jdbcType=INTEGER}


		AND a.times_unit = #{times_unit,jdbcType=INTEGER}


		AND a.is_fix = #{is_fix,jdbcType=INTEGER}


		AND a.is_accept = #{is_accept,jdbcType=INTEGER}


		AND a.is_bid = #{is_bid,jdbcType=INTEGER}


		AND a.note = #{note,jdbcType=VARCHAR}


		AND atd.ass_naturs = #{ass_nature,jdbcType=VARCHAR}


			AND a.contract_id in (${contract_ids})


		group by  
		a.group_id,
		a.hos_id,
		a.copy_code,
	    a.ass_id,
	    a.ass_no,
	    a.ass_spec,
	    a.ass_model,
	    a.ass_brand,
	    a.fac_id,
	    a.fac_no,
	    atd.ass_naturs 
		order by a.group_id asc


		SELECT distinct
    a.group_id,
    a.hos_id,
    a.copy_code,
    a.contract_id,
    a.contract_detail_id,
    a.contract_no,
    a.ass_id ||'@'|| a.ass_no ass_id,
    a.ass_no,
    an.ass_code,
    an.ass_name,
    a.ass_spec,
    a.ass_model,
    a.ass_brand,
    a.fac_id ||'@'|| a.fac_no fac_id,
    a.fac_no,
    h.fac_code,
    h.fac_name,
    a.contract_amount,
    a.contract_price,
    a.send_date,
    a.keep_repair_times,
    a.times_unit,
    a.is_fix,
    a.is_accept,
    a.is_bid,
    a.note
    FROM ASS_CONTRACT_DETAIL a
    left join ass_no_dict an on a.group_id=an.group_id and a.hos_id=an.hos_id and a.ass_id=an.ass_id and an.is_stop=0
    left join hos_fac h on a.group_id=h.group_id and a.hos_id=h.hos_id and a.fac_id=h.fac_id


		AND a.group_id = #{group_id,jdbcType=INTEGER}


		AND a.hos_id = #{hos_id,jdbcType=INTEGER}


		AND a.copy_code = #{copy_code,jdbcType=VARCHAR}


		AND a.contract_id = #{contract_id,jdbcType=INTEGER}


		AND a.contract_detail_id = #{contract_detail_id,jdbcType=INTEGER}


		AND a.contract_no = #{contract_no,jdbcType=VARCHAR}


		AND a.ass_id = #{ass_id,jdbcType=INTEGER}


		AND a.ass_no = #{ass_no,jdbcType=INTEGER}


		AND a.ass_spec = #{ass_spec,jdbcType=VARCHAR}


		AND a.ass_model = #{ass_model,jdbcType=VARCHAR}


		AND a.ass_brand = #{ass_brand,jdbcType=VARCHAR}


		AND a.fac_id = #{fac_id,jdbcType=INTEGER}


		AND a.fac_no = #{fac_no,jdbcType=INTEGER}


		AND a.contract_amount = #{contract_amount,jdbcType=INTEGER}


		AND a.contract_price = #{contract_price,jdbcType=INTEGER}


		AND a.send_date = #{send_date,jdbcType=DATE}


		AND a.keep_repair_times = #{keep_repair_times,jdbcType=INTEGER}


		AND a.times_unit = #{times_unit,jdbcType=INTEGER}


		AND a.is_fix = #{is_fix,jdbcType=INTEGER}


		AND a.is_accept = #{is_accept,jdbcType=INTEGER}


		AND a.is_bid = #{is_bid,jdbcType=INTEGER}


		AND a.note = #{note,jdbcType=VARCHAR}


		AND not exists (select 1 from ASS_INS_CONTRACT_MAP bb where  a.group_id=bb.group_id and a.hos_id = bb.hos_id and a.copy_code=bb.copy_code and a.contract_id = bb.contract_id)



		order by group_id asc


