mapper.select:
SELECT acm.group_id, acm.hos_id, acm.copy_code, acm.contract_id, acm.contract_no, acm.contract_type, acm.contract_ori_no, acm.contract_name, acm.ass_year, acm.ass_month, acm.ven_id, acm.ven_no, acm.sign_date, acm.buy_type, acm.start_date, acm.end_date, acm.contract_detail, acm.service_detail, acm.emp_main, acm.provider, acm.is_bid, acm.contract_money, acm.create_emp, acm.create_date, acm.check_emp, acm.check_date, acm.state, acm.is_group, acm.cur_code, hsd.sup_code ven_code, hsd.sup_name ven_name, su.user_name emp_main_name, su1.user_name create_emp_name, su2.user_name check_emp_name FROM ASS_CONTRACT_MAIN acm LEFT JOIN ( select acd.contract_id,acd.group_id,acd.hos_id,acd.copy_code from ass_contract_detail acd LEFT JOIN ass_no_dict ad ON acd.ass_id = ad.ass_id AND acd.group_id = ad.group_id AND acd.hos_id = ad.hos_id AND acd.copy_code = ad.copy_code AND ad.is_stop = 0 LEFT JOIN ass_type_dict atd ON ad.ass_type_id = atd.ass_type_id AND ad.group_id = atd.group_id AND ad.hos_id = atd.hos_id AND ad.copy_code = atd.copy_code where atd.ass_naturs= #{ass_naturs,jdbcType=VARCHAR} group by acd.contract_id,acd.group_id,acd.hos_id,acd.copy_code ) det ON acm.contract_id = det.contract_id AND acm.group_id = det.group_id AND acm.hos_id = det.hos_id AND acm.copy_code = det.copy_code left join HOS_SUP_DICT hsd on acm.group_id=hsd.group_id and acm.hos_id=hsd.hos_id and acm.ven_id=hsd.sup_id and acm.ven_no=hsd.sup_no left join SYS_USER su on acm.group_id=su.group_id and acm.hos_id=su.hos_id and acm.emp_main=su.user_id left join Sys_User su1 on acm.group_id=su1.group_id and acm.hos_id=su1.hos_id and acm.create_emp=su1.user_id left join Sys_User su2 on acm.group_id=su2.group_id and acm.hos_id=su2.hos_id and acm.check_emp=su2.user_id
AND acm.group_id = #{group_id,jdbcType=INTEGER}
AND acm.hos_id = #{hos_id,jdbcType=INTEGER}
AND acm.copy_code = #{copy_code,jdbcType=VARCHAR}
AND acm.contract_id = #{contract_id,jdbcType=INTEGER}
AND acm.contract_no like '%${contract_no}%'
AND acm.contract_type = #{contract_type,jdbcType=VARCHAR}
AND acm.contract_ori_no like '%${contract_ori_no}%'
AND acm.contract_name like '%${contract_name}%'
AND acm.ass_year = #{ass_year,jdbcType=VARCHAR}
AND acm.ass_month between #{ass_month1} and #{ass_month2}
AND acm.ven_id = #{ven_id,jdbcType=INTEGER}
AND acm.ven_no = #{ven_no,jdbcType=INTEGER}
AND acm.sign_date between to_date(#{sign_date1},'yyyy/MM/dd') and to_date(#{sign_date2},'yyyy/MM/dd')
AND acm.buy_type = #{buy_type,jdbcType=VARCHAR}
AND acm.start_date = #{start_date,jdbcType=DATE}
AND acm.end_date = #{end_date,jdbcType=DATE}
AND acm.contract_detail = #{contract_detail,jdbcType=VARCHAR}
AND acm.service_detail = #{service_detail,jdbcType=VARCHAR}
AND acm.emp_main = #{emp_main,jdbcType=INTEGER}
AND acm.provider = #{provider,jdbcType=VARCHAR}
AND acm.is_bid = #{is_bid,jdbcType=INTEGER}
AND acm.contract_money = #{contract_money,jdbcType=INTEGER}
AND acm.create_emp = #{create_emp,jdbcType=INTEGER}
AND acm.create_date between to_date(#{create_date1},'yyyy/MM/dd') and to_date(#{create_date2},'yyyy/MM/dd')
AND acm.check_emp = #{check_emp,jdbcType=INTEGER}
AND acm.check_date between to_date(#{check_date1},'yyyy/MM/dd') and to_date(#{check_date2},'yyyy/MM/dd')
AND acm.state = #{state,jdbcType=INTEGER}
AND acm.is_group = #{is_group,jdbcType=INTEGER}
AND acm.is_init = #{is_init,jdbcType=INTEGER}
AND not exists(select 1 from ass_in_contract_map where group_id = #{group_id} and hos_id = #{hos_id} and copy_code = #{copy_code} and contract_id = acm.contract_id)
AND not exists(select 1 from ASS_CONTRACT_GROUP_HOS where group_id = #{group_id} and hos_id = #{hos_id} and copy_code = #{copy_code} and HOS_CONTRACT_ID = acm.contract_id)
AND not exists(select 1 from ASS_ACCEPT_CONTRACT_MAP where group_id = #{group_id} and hos_id = #{hos_id} and copy_code = #{copy_code} and CONTRACT_ID = acm.contract_id)
AND not exists(select 1 from ASS_INS_CONTRACT_MAP where group_id = #{group_id} and hos_id = #{hos_id} and copy_code = #{copy_code} and CONTRACT_ID = acm.contract_id)
order by contract_no desc


