mapper.insert:
INSERT INTO ASS_FILE_INIT_HOUSE ( group_id , hos_id , copy_code , ass_card_no , file_code , file_name , equi_usage_code , file_url , location , spell_code , wbx_code , is_stop
) select t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.ass_card_no,jdbcType=VARCHAR} , #{item.file_code,jdbcType=VARCHAR} , #{item.file_name,jdbcType=VARCHAR} , #{item.equi_usage_code,jdbcType=VARCHAR} , #{item.file_url,jdbcType=VARCHAR} , #{item.location,jdbcType=VARCHAR} , #{item.spell_code,jdbcType=VARCHAR} , #{item.wbx_code,jdbcType=VARCHAR} , #{item.is_stop,jdbcType=INTEGER}
from dual
)t

