AND file_code = #{file_code,jdbcType=VARCHAR}


AND file_name = #{file_name,jdbcType=VARCHAR}


AND equi_usage_code = #{equi_usage_code,jdbcType=VARCHAR}


AND file_url = #{file_url,jdbcType=VARCHAR}


