mapper.select:

SELECT 
	a.group_id,
	a.hos_id,
	a.copy_code,
	a.ass_in_no,
	a.ass_id||'@'||a.ass_no ass_id,
	a.ass_no,
	b.ass_code,
	b.ass_name,
	a.ass_spec,
	a.ass_model,
	a.ass_brand,
	a.fac_id||'@'||a.fac_no fac_id,
	a.fac_no,
	c.fac_code||' '||c.fac_name fac_name,
	a.unit_code,
	hu.unit_name,
	a.in_amount,
	a.price,
	a.add_depre,
	a.add_depre_month,
	a.cur_money,
	a.fore_money,
	a.note
FROM ASS_IN_REST_DETAIL_INASSETS a
left join ass_no_dict b on
a.group_id = b.group_id
and a.hos_id = b.hos_id
and a.copy_code = b.copy_code
and a.ass_id = b.ass_id
and b.is_stop = 0
left join hos_fac_dict c on
a.group_id = c.group_id
and a.hos_id = c.hos_id
and a.fac_id = c.fac_id
and c.is_stop = 0
left join hos_unit hu on
a.group_id = hu.group_id
and a.hos_id = hu.hos_id
and a.unit_code = hu.unit_code
and hu.is_stop = 0 


AND a.group_id = #{group_id,jdbcType=INTEGER}


AND a.hos_id = #{hos_id,jdbcType=INTEGER}


AND a.copy_code = #{copy_code,jdbcType=VARCHAR}


AND a.ass_in_no = #{ass_in_no,jdbcType=VARCHAR}


AND a.ass_id = #{ass_id,jdbcType=INTEGER}


AND a.ass_no = #{ass_no,jdbcType=INTEGER}


AND a.ass_spec = #{ass_spec,jdbcType=VARCHAR}


AND a.ass_model = #{ass_model,jdbcType=VARCHAR}


AND a.ass_brand = #{ass_brand,jdbcType=VARCHAR}


AND a.fac_id = #{fac_id,jdbcType=INTEGER}


AND a.fac_no = #{fac_no,jdbcType=INTEGER}


AND a.unit_code = #{unit_code,jdbcType=VARCHAR}


AND a.in_amount = #{in_amount,jdbcType=INTEGER}


AND a.price = #{price,jdbcType=INTEGER}


AND a.add_depre = #{add_depre,jdbcType=INTEGER}


AND a.add_depre_month = #{add_depre_month,jdbcType=INTEGER}


AND a.cur_money = #{cur_money,jdbcType=INTEGER}


AND a.fore_money = #{fore_money,jdbcType=INTEGER}


AND a.note = #{note,jdbcType=VARCHAR}


order by a.ass_id asc

SELECT a.group_id, a.hos_id, a.copy_code, a.ass_in_no, a.ass_id, a.ass_no, a.ass_spec, a.ass_model, a.ass_brand, a.fac_id, a.fac_no, a.unit_code, a.in_amount, a.price, a.add_depre, a.add_depre_month, a.cur_money, a.fore_money, a.note, b.is_measure, b.is_depre , b.ass_depre_code, b.depre_years, b.gb_code, b.is_bar, b.bar_type, b.is_manage_depre, b.manage_depr_method, b.manage_depre_amount, d.bus_type_code, d.store_id, d.store_no, d.dept_id, d.dept_no, d.ass_purpose, d.proj_id, d.proj_no, d.note, hsd.store_code, to_char(d.create_date,'YYYY') year, to_char(d.create_date,'MM') month FROM ASS_IN_REST_DETAIL_INASSETS a left join ass_no_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_id = b.ass_id and b.is_stop = 0 left join ASS_IN_REST_MAIN_INASSETS d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.copy_code = d.copy_code and a.ass_in_no = d.ass_in_no
left join ass_bus_type_dict abtd on d.group_id = abtd.group_id and d.hos_id = abtd.hos_id and d.copy_code = abtd.copy_code and d.bus_type_code = abtd.bus_type_code
left join hos_store_dict hsd on d.group_id = hsd.group_id and d.hos_id = hsd.hos_id and d.store_id = hsd.store_id and hsd.is_stop = 0
AND a.group_id = #{group_id,jdbcType=INTEGER}
AND a.hos_id = #{hos_id,jdbcType=INTEGER}
AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.ass_in_no = #{ass_in_no,jdbcType=VARCHAR}
AND a.ass_id = #{ass_id,jdbcType=INTEGER}
AND a.ass_no = #{ass_no,jdbcType=INTEGER}
AND a.ass_id in (${ass_ids})
AND a.ass_no in (${ass_nos})
order by a.ass_id asc


