mapper.select:
select a.group_id, a.hos_id, a.copy_code, a.ins_id, a.ins_no, a.ins_name, a.ass_year, a.ass_month, a.ass_nature, n.naturs_name, a.dept_id, a.dept_no, h.dept_name, a.create_emp, s.user_name create_emp_name, a.create_date, a.audit_emp, s1.user_name audit_emp_name, a.audit_date, a.state, a.note
from ass_inspection a left join (select group_id, hos_id, copy_code, ins_id, max(ass_card_no) ass_card_no from ass_inspection_detail group by group_id, hos_id, copy_code, ins_id
) aa on aa.group_id = a.group_id and aa.hos_id = a.hos_id and aa.copy_code = a.copy_code and aa.ins_id = a.ins_id left join v_ass_card ca on ca.GROUP_ID = aa.group_id and ca.HOS_ID = aa.hos_id and ca.COPY_CODE = aa.copy_code and ca.ASS_CARD_NO = aa.ass_card_no left join ass_no_dict di on di.group_id = ca.GROUP_ID and di.hos_id = ca.HOS_ID and di.copy_code = ca.COPY_CODE and di.ass_id = ca.ASS_ID and di.ass_no = ca.ASS_NO and di.is_stop = 0 left join HOS_DEPT_DICT h on a.dept_id = h.dept_id and a.group_id = h.group_id and a.hos_id = h.hos_id and a.dept_no = h.dept_no left join sys_user s on s.group_id = a.group_id and s.hos_id = a.hos_id and s.user_id = a.create_emp left join sys_user s1 on a.group_id = s1.group_id and a.hos_id = s1.hos_id and s1.user_id = a.audit_emp left join ASS_NATURS n on a.ass_nature = n.naturs_code
AND a.group_id = #{group_id,jdbcType=INTEGER}
AND a.hos_id = #{hos_id,jdbcType=INTEGER}
AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.ins_id = #{ins_id,jdbcType=INTEGER}
AND a.ins_no = #{ins_no,jdbcType=VARCHAR}
AND a.ins_name = #{ins_name,jdbcType=VARCHAR}
AND a.ass_year = #{ass_year,jdbcType=VARCHAR}
AND a.ass_month = #{ass_month,jdbcType=VARCHAR}
AND a.ass_nature = #{ass_nature,jdbcType=VARCHAR}
AND a.dept_id = #{dept_id,jdbcType=INTEGER}
AND a.dept_no = #{dept_no,jdbcType=INTEGER}
AND a.create_emp = #{create_emp,jdbcType=INTEGER}
AND a.create_date between to_date(#{create_date_begin,jdbcType=DATE}, 'yyyy-MM-dd') and to_date(#{create_date_end,jdbcType=DATE}, 'yyyy-MM-dd')
AND a.audit_emp = #{audit_emp,jdbcType=INTEGER}
AND a.audit_date between to_date(#{audit_date_begin,jdbcType=DATE}, 'yyyy-MM-dd') and to_date(#{audit_date_end,jdbcType=DATE}, 'yyyy-MM-dd')
AND a.state = #{state,jdbcType=INTEGER}
AND a.note = #{note,jdbcType=VARCHAR}
AND (di.ass_name like '%${ass_table}%' or aa.ass_card_no like '%${ass_table}%' or di.ass_code like '%${ass_table}%' )
order by a.group_id ASC


		select 
  a.group_id,
  a.hos_id,
  a.copy_code,
  a.ins_id,
  a.ins_no,
  a.ins_name,
  a.ass_year,
  a.ass_month,
  a.ass_nature,
  n.naturs_name,
  a.dept_id,
  a.dept_no,
  h.dept_name,
  a.create_emp,
  s.user_name create_emp_name,
  a.create_date,
  a.audit_emp,
  s1.user_name audit_emp_name,
  a.audit_date,
  a.state,
  a.note
from ASS_INSPECTION_DETAIL aid
right join ass_inspection a on a.ins_id=aid.ins_id
 left join HOS_DEPT_DICT h on a.dept_id=h.dept_id and a.group_id=h.group_id and a.hos_id=h.hos_id and a.dept_no=h.dept_no
 left join sys_user s on s.group_id=a.group_id and s.hos_id=a.hos_id and s.user_id=a.create_emp
 left join sys_user s1 on a.group_id=s1.group_id and a.hos_id=s1.hos_id and s1.user_id=a.audit_emp
 left join ASS_NATURS n on a.ass_nature=n.naturs_code

		aid.ass_card_no=#{ass_card_no,jdbcType=VARCHAR}                     

		AND a.group_id = #{group_id,jdbcType=INTEGER}


		AND a.hos_id = #{hos_id,jdbcType=INTEGER}


		AND a.copy_code = #{copy_code,jdbcType=VARCHAR}


		AND a.ins_id = #{ins_id,jdbcType=INTEGER}


		AND a.ins_no = #{ins_no,jdbcType=VARCHAR}

		AND a.ins_name = #{ins_name,jdbcType=VARCHAR}


		AND a.ass_year = #{ass_year,jdbcType=VARCHAR}


		AND a.ass_month = #{ass_month,jdbcType=VARCHAR}


		AND a.ass_nature = #{ass_nature,jdbcType=VARCHAR}


		AND a.dept_id = #{dept_id,jdbcType=INTEGER}


		AND a.dept_no = #{dept_no,jdbcType=INTEGER}


		AND a.create_emp = #{create_emp,jdbcType=INTEGER}


		AND a.create_date between  to_date(#{create_date_begin,jdbcType=DATE}, 'yyyy-MM-dd')  and to_date(#{create_date_end,jdbcType=DATE}, 'yyyy-MM-dd')  


		AND a.audit_emp = #{audit_emp,jdbcType=INTEGER}


		AND a.audit_date between to_date(#{audit_date_begin,jdbcType=DATE}, 'yyyy-MM-dd')  and to_date(#{audit_date_end,jdbcType=DATE}, 'yyyy-MM-dd')  


		AND a.state = #{state,jdbcType=INTEGER}


		AND a.note = #{note,jdbcType=VARCHAR}


		order by a.group_id ASC

select group_id, hos_id, copy_code, ins_id, ins_no, ins_name, ass_year, ass_month, ass_nature, dept_id, dept_no, create_emp, create_date, audit_emp, audit_date, state, note from ass_inspection
AND group_id = #{group_id,jdbcType=INTEGER}
AND hos_id = #{hos_id,jdbcType=INTEGER}
AND copy_code = #{copy_code,jdbcType=VARCHAR}
AND ins_id = #{ins_id,jdbcType=INTEGER}
order by group_id ASC


		select 
          group_id,
          hos_id,
          copy_code,
          ins_id,
          ins_no,
          ins_name,
          ass_year,
          ass_month,
          ass_nature,
          dept_id,
          dept_no,
          create_emp,
          create_date,
          audit_emp,
          audit_date,
          state,
          note
         from ass_inspection


		AND group_id = #{group_id,jdbcType=INTEGER}


		AND hos_id = #{hos_id,jdbcType=INTEGER}


		AND copy_code = #{copy_code,jdbcType=VARCHAR}


		AND ins_id = #{ins_id,jdbcType=INTEGER}


		AND ins_no = #{ins_no,jdbcType=VARCHAR}


		AND ass_year = #{ass_year,jdbcType=VARCHAR}


		AND ass_month = #{ass_month,jdbcType=VARCHAR}


		AND ins_date = #{ins_date,jdbcType=DATE}


		AND summary = #{summary,jdbcType=VARCHAR}


		AND contract_id = #{contract_id,jdbcType=INTEGER}


		AND ven_id = #{ven_id,jdbcType=INTEGER}


		AND ven_no = #{ven_no,jdbcType=INTEGER}


		AND dept_id = #{dept_id,jdbcType=INTEGER}


		AND dept_no = #{dept_no,jdbcType=INTEGER}


		AND accept_desc = #{accept_desc,jdbcType=VARCHAR}


		AND create_emp = #{create_emp,jdbcType=INTEGER}


		AND create_date = #{create_date,jdbcType=DATE}


		AND audit_emp = #{audit_emp,jdbcType=INTEGER}


		AND audit_date = #{audit_date,jdbcType=DATE}


		AND state = #{state,jdbcType=INTEGER}


		order by group_id asc

SELECT an.naturs_code||' '||an.naturs_name ass_nature, an.naturs_code naturs_code, a.ass_card_no , b.ass_code, b.ass_name, b.ass_spec, b.ass_model, b.ass_brand, hu.unit_name ass_unit_name, hfd.fac_code||' '||hfd.fac_name fac_name, hsd.sup_code||' '||hsd.sup_name ven_name, a.price, a.depre_money, a.manage_depre_money, a.cur_money, a.fore_money, a.add_depre_month, a.add_manage_month, a.buy_type, a.use_state, a.in_date, a.ven_id, a.ven_no, null store_id, null store_no, null is_dept FROM ass_card_land a left join ass_no_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_id = b.ass_id and b.is_stop = 0 and b.is_check = 1 left join ass_type_dict c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_type_id = c.ass_type_id and c.is_stop = 0
left join ass_naturs an on c.ass_naturs = an.naturs_code
left join hos_unit hu on b.group_id=hu.group_id and b.hos_id=hu.hos_id and b.ass_unit=hu.unit_code and hu.is_stop=0
left join hos_fac_dict hfd on b.group_id = hfd.group_id and b.hos_id = hfd.hos_id and b.fac_id = hfd.fac_id and hfd.is_stop = 0
left join hos_sup_dict hsd on a.group_id = hsd.group_id and a.hos_id = hsd.hos_id and a.ven_id = hsd.sup_id and hsd.is_stop = 0
left join ( select t1.group_id, t1.hos_id, t1.copy_code, t1.ass_card_no, nvl(sum(t1.unpay_money),0) unpay_money, nvl(sum(t1.pay_money),0) pay_money , nvl(sum(t1.pay_plan_money),0) pay_plan_money
from ass_pay_stage_land t1 left join ass_card_land t2 on t1.group_id = t2.group_id and t1.hos_id = t2.hos_id and t1.copy_code = t2.copy_code and t1.ass_card_no = t2.ass_card_no group by t1.group_id, t1.hos_id, t1.copy_code, t1.ass_card_no ) pay on a.group_id = pay.group_id and a.hos_id = pay.hos_id and a.copy_code = pay.copy_code and a.ass_card_no = pay.ass_card_no
and a.group_id = #{group_id} and a.hos_id = #{hos_id} and a.copy_code = #{copy_code} and b.is_check = 1
and an.naturs_code = #{ass_nature}
and a.buy_type = #{bus_type_code}
and a.ass_card_no like '%${ass_card_no}%'
and b.ass_name like '%${ass_name}%'
and a.dept_id = #{dept_id}
and a.dept_no = #{dept_no}
and a.use_state in (${use_state})
and a.in_date <= to_date(#{in_date},'YYYY-MM-DD')
and a.store_id = #{store_id}
and a.store_no = #{store_no}
and a.ven_id = #{ven_id}
and a.ven_no = #{ven_no}
and a.is_dept = #{is_dept}
and a.contract_id = #{contract_id}
and exists(
select 1 from ass_share_dept_land t1 where a.hos_id = t1.hos_id and a.copy_code = t1.copy_code and a.ass_card_no = t1.ass_card_no and t1.dept_id = #{in_dept_id} and t1.dept_no = #{in_dept_no} )
and pay.pay_plan_money - pay.pay_money > 0
and pay.pay_money > 0
and ( a.ass_card_no like '%${key}%' or a.ass_id like '%${key}%' or b.ass_code like '%${key}%' or b.ass_name like '%${key}%' or an.naturs_code like '%${key}%' or b.spell_code like '%${key}%' or b.wbx_code like '%${key}%' )
order by a.ass_card_no

