left join hos_fac_dict hfd on b.group_id = hfd.group_id and b.hos_id = hfd.hos_id and b.fac_id = hfd.fac_id and hfd.is_stop = 0
left join hos_sup_dict hsd on a.group_id = hsd.group_id and a.hos_id = hsd.hos_id and a.ven_id = hsd.sup_id and hsd.is_stop = 0
left join ( select t1.group_id, t1.hos_id, t1.copy_code, t1.ass_card_no, nvl(sum(t1.unpay_money),0) unpay_money, nvl(sum(t1.pay_money),0) pay_money , nvl(sum(t1.pay_plan_money),0) pay_plan_money
from ass_pay_stage_HOUSE t1 left join ass_card_HOUSE t2 on t1.group_id = t2.group_id and t1.hos_id = t2.hos_id and t1.copy_code = t2.copy_code and t1.ass_card_no = t2.ass_card_no group by t1.group_id, t1.hos_id, t1.copy_code, t1.ass_card_no ) pay on a.group_id = pay.group_id and a.hos_id = pay.hos_id and a.copy_code = pay.copy_code and a.ass_card_no = pay.ass_card_no
and a.group_id = #{group_id} and a.hos_id = #{hos_id} and a.copy_code = #{copy_code} and b.is_check = 1
and an.naturs_code = #{ass_nature}
and a.buy_type = #{bus_type_code}
and a.ass_card_no like '%${ass_card_no}%'
and b.ass_name like '%${ass_name}%'
and a.dept_id = #{dept_id}
and a.dept_no = #{dept_no}
and a.use_state in (${use_state})
and a.in_date <= to_date(#{in_date},'YYYY-MM-DD')
and a.store_id = #{store_id}
and a.store_no = #{store_no}
and a.ven_id = #{ven_id}
and a.ven_no = #{ven_no}
and a.is_dept = #{is_dept}
and a.contract_id = #{contract_id}
and exists(
select 1 from ass_share_dept_HOUSE t1 where a.hos_id = t1.hos_id and a.copy_code = t1.copy_code and a.ass_card_no = t1.ass_card_no and t1.dept_id = #{in_dept_id} and t1.dept_no = #{in_dept_no} )
and pay.pay_plan_money - pay.pay_money > 0
and pay.pay_money > 0
and ( a.ass_card_no like '%${key}%' or a.ass_id like '%${key}%' or b.ass_code like '%${key}%' or b.ass_name like '%${key}%' or an.naturs_code like '%${key}%' or b.spell_code like '%${key}%' or b.wbx_code like '%${key}%' )
order by a.ass_card_no

