select a.ins_id as id, a.group_id, a.hos_id, a.copy_code, b.ASS_CARD_NO, c.ass_code, c.ass_name, b.ASS_SPEC, b.ASS_MONDL, b.ASS_BRAND, d.fac_name, case when a.state = 0 then '否' when a.state = 1 then '是' else '' end state_name, case when a.is_rep = 0 then '否' when a.is_rep = 1 then '是' else '' end is_rep, case when a.is_main = 0 then '否' when a.is_main = 1 then '是' else '' end is_main, case when a.ins_result = 0 then '未完成' when a.ins_result = 1 then '完成' else '' end ins_result, a.note note_detail, b.ASS_SEQ_NO from ass_inspection_detail a left join v_ass_card b on a.group_id = b.GROUP_ID and a.hos_id = b.HOS_ID and a.copy_code = b.COPY_CODE and a.ass_card_no = b.ASS_CARD_NO left join ass_no_dict c on c.group_id = b.GROUP_ID and c.hos_id = b.HOS_ID and c.copy_code = b.COPY_CODE and c.ass_id = b.ASS_ID and c.ass_no = b.ASS_NO and c.is_stop = 0 left join hos_fac_dict d on d.group_id = b.GROUP_ID and d.hos_id = b.HOS_ID and d.fac_id = b.FAC_ID and d.fac_no = b.FAC_NO and d.is_stop = 0 where a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.ins_id in (${paraId})
AND a.ins_id = #{ins_id,jdbcType=VARCHAR}
order by a.ins_id asc

SELECT ins_no FROM ass_inspection 
	WHERE
		group_id = #{group_id,jdbcType=INTEGER}
		AND hos_id = #{hos_id,jdbcType=INTEGER}
		AND copy_code = #{copy_code,jdbcType=VARCHAR}

		AND ins_id in  (${paraId})


		AND ins_id = #{ins_id,jdbcType=VARCHAR}


		and state != #{state,jdbcType=INTEGER}

with detail_temp as (select a.group_id, a.hos_id, a.copy_code, a.ins_id, a.ins_no, a.ins_name, a.ass_year, a.ass_month, a.ass_nature, n.naturs_name, a.dept_id, a.dept_no, h.dept_name, a.create_emp, s.user_name create_emp_name, a.create_date, a.audit_emp, s1.user_name audit_emp_name, a.audit_date, a.state, a.note, b.ass_card_no, c.ASS_SPEC, c.ASS_BRAND, c.ASS_MONDL, c.ASS_SEQ_NO, e.fac_name, d.ass_code, d.ass_name, b.state state_go, b.is_rep, b.is_main, b.ins_result from ass_inspection a left join ass_inspection_detail b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ins_id = b.ins_id left join v_ass_card c on c.GROUP_ID = b.group_id and c.HOS_ID = b.hos_id and c.COPY_CODE = b.copy_code and c.ASS_CARD_NO = b.ass_card_no left join ass_no_dict d on d.group_id = c.GROUP_ID and d.hos_id = c.HOS_ID and d.copy_code = c.COPY_CODE and d.ass_id = c.ASS_ID and d.ass_no = c.ASS_NO and d.is_stop = 0 left join hos_fac_dict e on e.group_id = c.GROUP_ID and e.hos_id = c.HOS_ID and e.fac_id = c.FAC_ID and e.fac_no = c.FAC_NO and e.is_stop = 0 left join HOS_DEPT_DICT h on a.dept_id = h.dept_id and a.group_id = h.group_id and a.hos_id = h.hos_id and a.dept_no = h.dept_no left join sys_user s on s.group_id = a.group_id and s.hos_id = a.hos_id and s.user_id = a.create_emp left join sys_user s1 on a.group_id = s1.group_id and a.hos_id = s1.hos_id and s1.user_id = a.audit_emp left join ASS_NATURS n on a.ass_nature = n.naturs_code
AND a.group_id = #{group_id,jdbcType=INTEGER}
AND a.hos_id = #{hos_id,jdbcType=INTEGER}
AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.ins_id = #{ins_id,jdbcType=INTEGER}
AND a.ins_no = #{ins_no,jdbcType=VARCHAR}
AND a.ins_name = #{ins_name,jdbcType=VARCHAR}
AND a.ass_year = #{ass_year,jdbcType=VARCHAR}
AND a.ass_month = #{ass_month,jdbcType=VARCHAR}
AND a.ass_nature = #{ass_nature,jdbcType=VARCHAR}
AND a.dept_id = #{dept_id,jdbcType=INTEGER}
AND a.dept_no = #{dept_no,jdbcType=INTEGER}
AND a.create_emp = #{create_emp,jdbcType=INTEGER}
AND a.create_date between to_date(#{create_date_begin,jdbcType=DATE}, 'yyyy-MM-dd') and to_date(#{create_date_end,jdbcType=DATE}, 'yyyy-MM-dd')
AND a.audit_emp = #{audit_emp,jdbcType=INTEGER}
AND a.audit_date between to_date(#{audit_date_begin,jdbcType=DATE}, 'yyyy-MM-dd') and to_date(#{audit_date_end,jdbcType=DATE}, 'yyyy-MM-dd')
AND a.state = #{state,jdbcType=INTEGER}
AND a.note = #{note,jdbcType=VARCHAR}
AND (d.ass_name like '%${ass_table}%' or b.ass_card_no like '%${ass_table}%' or d.ass_code like '%${ass_table}%' )
) select group_id, hos_id, copy_code, ins_id, ins_no, ins_name, ass_year, ass_month, ass_nature, naturs_name, dept_id, dept_no, dept_name, create_emp, create_emp_name, create_date, audit_emp, audit_emp_name, audit_date, state, note, ass_card_no, ASS_SPEC, ASS_BRAND, ASS_MONDL, ASS_SEQ_NO, fac_name, ass_code, ass_name, state_go, is_rep, is_main, ins_result from detail_temp order by ins_id
