mapper.select:

SELECT 
	a.group_id,
	a.hos_id,
	a.copy_code,
	a.sell_in_no,
	a.ass_id||'@'||a.ass_no ass_id,
	a.ass_no,
	b.ass_code,
	b.ass_name,
	a.ass_ori_card_no,
	a.ass_brand,
	a.ass_spec,
	a.ass_model,
	a.fac_id||'@'||a.fac_no fac_id,
	a.fac_no,
	c.fac_code||' '||c.fac_name fac_name,
	a.price,
	a.add_depre,
	a.add_depre_month,
	a.cur_money,
	a.fore_money,
	a.note
FROM ass_sell_in_detail_Other a

left join ass_no_dict b on
a.group_id = b.group_id
and a.hos_id = b.hos_id
and a.copy_code = b.copy_code
and a.ass_id = b.ass_id
and b.is_stop = 0
left join hos_fac_dict c on
a.group_id = c.group_id
and a.hos_id = c.hos_id
and a.fac_id = c.fac_id
and c.is_stop = 0


	AND a.group_id = #{group_id,jdbcType=INTEGER}


	AND a.hos_id = #{hos_id,jdbcType=INTEGER}


	AND a.copy_code = #{copy_code,jdbcType=VARCHAR}


	AND a.sell_in_no = #{sell_in_no,jdbcType=VARCHAR}


	AND a.ass_id = #{ass_id,jdbcType=INTEGER}


	AND a.ass_no = #{ass_no,jdbcType=INTEGER}


	AND a.ass_ori_card_no = #{ass_ori_card_no,jdbcType=VARCHAR}


	AND a.ass_brand = #{ass_brand,jdbcType=VARCHAR}


	AND a.ass_spec = #{ass_spec,jdbcType=VARCHAR}


	AND a.ass_model = #{ass_model,jdbcType=VARCHAR}


	AND a.fac_id = #{fac_id,jdbcType=INTEGER}


	AND a.fac_no = #{fac_no,jdbcType=INTEGER}


	AND a.price = #{price,jdbcType=INTEGER}


	AND a.add_depre = #{add_depre,jdbcType=INTEGER}


	AND a.add_depre_month = #{add_depre_month,jdbcType=INTEGER}


	AND a.cur_money = #{cur_money,jdbcType=INTEGER}


	AND a.fore_money = #{fore_money,jdbcType=INTEGER}


	AND a.note = #{note,jdbcType=VARCHAR}


order by a.ass_id asc

SELECT a.group_id, a.hos_id, a.copy_code, a.sell_in_no, a.sell_in_no ass_in_no, a.ass_ori_card_no, a.ass_id, a.ass_no, a.ass_spec, a.ass_model, a.ass_brand, a.fac_id, a.fac_no, b.ass_unit unit_code, a.price, a.add_depre, a.add_depre_month, a.cur_money, a.fore_money, a.note, b.is_measure, b.is_depre , b.ass_depre_code, b.depre_years, b.gb_code, b.is_bar, b.bar_type, b.is_manage_depre, b.manage_depr_method, b.manage_depre_amount, d.bus_type_code, d.in_store_id, d.in_store_no, d.out_group_id, d.out_hos_id, d.out_store_id, d.out_store_no FROM ASS_SELL_IN_DETAIL_Other a left join ass_no_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_id = b.ass_id and b.is_stop = 0 left join ass_sell_in_Other d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.copy_code = d.copy_code and a.sell_in_no = d.sell_in_no
AND a.group_id = #{group_id,jdbcType=INTEGER}
AND a.hos_id = #{hos_id,jdbcType=INTEGER}
AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.sell_in_no = #{sell_in_no,jdbcType=VARCHAR}
AND a.ass_id = #{ass_id,jdbcType=INTEGER}
AND a.ass_no = #{ass_no,jdbcType=INTEGER}
AND a.ass_id in (${ass_ids})
AND a.ass_no in (${ass_nos})
order by a.ass_id asc


