select * from ( SELECT a.group_id, a.hos_id, a.copy_code, a.sell_in_no, a.in_store_id, a.in_store_no, in_store.store_code in_store_code, in_store.store_name in_store_name, a.out_group_id, sg.group_name out_group_name, a.out_hos_id, hi.hos_name out_hos_name, a.out_store_id, a.out_store_no, out_store.store_code out_store_code, out_store.store_name out_store_name, a.price, a.add_depre, a.cur_money, a.fore_money, a.create_emp, su1.user_name create_emp_name, a.create_date, a.audit_emp, su2.user_name audit_emp_name, a.audit_date, a.state, case when a.state = 0 then '新建' when a.state = 1 then '审核' when a.state = 2 then '入库确认' else '' end state_name, a.note, nvl(alms.is_import, 0) as is_import, a.bus_type_code, abtd.bus_type_name FROM ass_sell_in_General a left join hos_store_dict in_store on a.group_id = in_store.group_id and a.hos_id = in_store.hos_id and a.in_store_id = in_store.store_id and in_store.is_stop = 0
left join sys_group sg on a.out_group_id = sg.group_id
left join hos_info hi on a.out_group_id = hi.group_id and a.out_hos_id = hi.hos_id
left join hos_store_dict out_store on a.out_group_id = out_store.group_id and a.out_hos_id = out_store.hos_id and a.out_store_id = out_store.store_id and out_store.is_stop = 0
left join sys_user su1 on a.group_id = su1.group_id and a.hos_id = su1.hos_id and a.create_emp = su1.user_id
left join sys_user su2 on a.group_id = su2.group_id and a.hos_id = su2.hos_id and a.audit_emp = su2.user_id
left join ass_bus_type_dict abtd on a.group_id = abtd.group_id and a.hos_id = abtd.hos_id and a.copy_code = abtd.copy_code and a.bus_type_code = abtd.bus_type_code
left join (select a.group_id, a.hos_id, a.copy_code, a.sell_in_no, case when count(1) > 0 then 1 else 0 end as is_import from ass_sell_map_General a left join ASS_SELL_IN_General b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.sell_in_no = b.sell_in_no group by a.group_id, a.hos_id, a.copy_code, a.sell_in_no) alms on a.group_id = alms.group_id and a.hos_id = alms.hos_id and a.copy_code = alms.copy_code and a.sell_in_no = alms.sell_in_no
AND a.group_id = #{group_id}
AND a.hos_id = #{hos_id}
AND a.copy_code = #{copy_code}
AND a.sell_in_no = #{sell_in_no}
AND a.in_store_id = #{in_store_id}
AND a.in_store_no = #{in_store_no}
AND a.out_group_id = #{out_group_id}
AND a.out_hos_id = #{out_hos_id}
AND a.out_store_id = #{out_store_id}
AND a.out_store_no = #{out_store_no}
AND a.price = #{price}
AND a.add_depre = #{add_depre}
AND a.cur_money = #{cur_money}
AND a.fore_money = #{fore_money}
