AND a.state = #{state}
AND a.bus_type_code = #{bus_type_code}
AND a.note = #{note}
union all SELECT null group_id, null hos_id, null copy_code, null sell_in_no, null in_store_id, null in_store_no, null in_store_code, null in_store_name, null out_group_id, null out_group_name, null out_hos_id, null out_hos_name, null out_store_id, null out_store_no, null out_store_code, null out_store_name, sum(a.price) price, sum(a.add_depre) add_depre, sum(a.cur_money) cur_money, sum(a.fore_money) fore_money, null create_emp, null create_emp_name, null create_date, null audit_emp, null audit_emp_name, null audit_date, null state, null state_name, '合计' note, 0 is_import, null bus_type_code, null bus_type_name FROM ass_sell_in_General a
AND a.group_id = #{group_id}
