select
t5.sell_in_no as id , t5.ass_id , t5.ass_no, b.ass_code, b.ass_name, t5.ass_ori_card_no, t5.ass_brand, t5.ass_spec, t5.ass_model, t5.fac_id, t5.fac_no, c.fac_code, c.fac_name , to_char(t5.price, 'FM999,999,990.00') price_detail, to_char(t5.add_depre, 'FM999,999,990.00') add_depre_detail , t5.add_depre_month, to_char(t5.cur_money, 'FM999,999,990.00') cur_money_detail , to_char(t5.fore_money, 'FM999,999,990.00') fore_money_detail , t5.note note_detail, to_char(price_sum, 'FM999,999,990.00') price_sum, to_char(zc_price, 'FM999,999,990.00') zc_price, to_char(cz_price, 'FM999,999,990.00') cz_price, to_char(ky_price, 'FM999,999,990.00') ky_price, to_char(jx_price, 'FM999,999,990.00') jx_price from (select t3.source_id, t3.group_id, t3.hos_id, t3.copy_code, sum(t3.price) sum_price, sum(t2.price) price_sum, t2.ass_id, t2.ass_no, t2.ass_in_no from ass_card_General t2
left join ass_resource_General t3 on t3.ass_card_no = t2.ass_card_no and t3.group_id = t2.group_id and t3.hos_id = t3.hos_id and t3.copy_code = t2.copy_code where t2.group_id = #{group_id,jdbcType=INTEGER} AND t2.hos_id = #{hos_id,jdbcType=INTEGER} AND t2.copy_code = #{copy_code,jdbcType=VARCHAR}
AND t2.ass_in_no in (${paraId})
AND t2.ass_in_no = #{sell_in_no,jdbcType=VARCHAR}
group by t3.source_id, t2.ass_id, t2.ass_no, t3.group_id, t3.hos_id, t2.ass_in_no, t3.copy_code) pivot(sum(sum_price) as price for source_id in('1' as ZC, '2' as CZ, '3' as KY, '4' as JX)) t left join ass_sell_in_detail_General t5 on t.ass_id = t5.ass_id and t5.group_id = t.group_id and t5.hos_id = t.hos_id and t5.copy_code = t.copy_code and t5.sell_in_no = t.ass_in_no left join ass_no_dict b on t5.group_id = b.group_id and t5.hos_id = b.hos_id and t5.copy_code = b.copy_code and t5.ass_id = b.ass_id and b.is_stop = 0 left join hos_fac_dict c on t5.group_id = c.group_id and t5.hos_id = c.hos_id and t5.fac_id = c.fac_id and c.is_stop = 0 where t5.group_id = #{group_id,jdbcType=INTEGER} AND t5.hos_id = #{hos_id,jdbcType=INTEGER} AND t5.copy_code = #{copy_code,jdbcType=VARCHAR}
AND t5.sell_in_no in (${paraId})
AND t5.sell_in_no = #{sell_in_no,jdbcType=VARCHAR}
group by t5.sell_in_no, t5.ass_id, t5.ass_no, b.ass_code, b.ass_name, t5.ass_ori_card_no, t5.ass_brand, t5.ass_spec, t5.ass_model, t5.fac_id, t5.fac_no, c.fac_code, c.fac_name, t5.price, t5.add_depre, t5.add_depre_month, t5.cur_money, t5.fore_money, t5.note, price_sum, zc_price, cz_price, ky_price, jx_price order by t5.sell_in_no asc

