from ass_sell_in_General a left join ASS_sell_IN_DETAIL_General b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.sell_in_no = b.sell_in_no left join hos_store_dict in_store on a.group_id = in_store.group_id and a.hos_id = in_store.hos_id and a.in_store_id = in_store.store_id and in_store.is_stop = 0
