AND a.hos_id = #{hos_id}
AND a.copy_code = #{copy_code}
AND a.sell_out_no = #{sell_out_no}
AND a.out_store_id = #{out_store_id}
AND a.out_store_no = #{out_store_no}
AND a.in_group_id = #{in_group_id}
AND a.in_hos_id = #{in_hos_id}
AND a.in_store_id = #{in_store_id}
AND a.in_store_no = #{in_store_no}
AND a.price = #{price}
AND a.add_depre = #{add_depre}
AND a.cur_money = #{cur_money}
AND a.fore_money = #{fore_money}
AND a.sell_price = #{sell_price}
AND a.dispose_income = #{dispose_income}
AND a.dispose_tax = #{dispose_tax}
AND a.create_emp = #{create_emp}
AND a.create_date between to_date(#{create_date_beg},'YYYY-MM-DD') and to_date(#{create_date_end},'YYYY-MM-DD')
AND a.audit_emp = #{audit_emp}
AND a.audit_date between to_date(#{audit_date_beg},'YYYY-MM-DD') and to_date(#{audit_date_end},'YYYY-MM-DD')
AND a.state = #{state}
AND a.note = #{note}
AND a.bus_type_code = #{bus_type_code}
), sum_tmp as (select null group_id, null hos_id, null copy_code, null sell_out_no, null out_store_id, null out_store_no, null out_store_code, null out_store_name, null in_group_id, null in_group_name, null in_hos_id, null in_hos_name, null in_store_id, null in_store_no, null in_store_code, null in_store_name, sum(price) price, sum(add_depre) add_depre, sum(cur_money) cur_money, sum(fore_money) fore_money, null create_emp, null create_emp_name, null create_date, null audit_emp, null audit_emp_name, null audit_date, null state, null bus_type_code, null bus_type_name, null state_name, '合计' note, sum(add_manage_depre) add_manage_depre, null add_depre_month, sum(now_depre) now_depre, sum(now_manage_depre) now_manage_depre, null ass_card_no, null ass_id, null ass_no, null ass_code, null ass_name from detail_tmp)
select group_id, hos_id, copy_code, sell_out_no, out_store_id, out_store_no, out_store_code, out_store_name, in_group_id, in_group_name, in_hos_id, in_hos_name, in_store_id, in_store_no, in_store_code, in_store_name, price, add_depre, cur_money, fore_money, create_emp, create_emp_name, create_date, audit_emp, audit_emp_name, audit_date, state, bus_type_code, bus_type_name, state_name, note, add_manage_depre, add_depre_month, now_depre, now_manage_depre, ass_card_no, ass_id, ass_no, ass_code, ass_name from detail_tmp union all select group_id, hos_id, copy_code, sell_out_no, out_store_id, out_store_no, out_store_code, out_store_name, in_group_id, in_group_name, in_hos_id, in_hos_name, in_store_id, in_store_no, in_store_code, in_store_name, price, add_depre, cur_money, fore_money, create_emp, create_emp_name, create_date, audit_emp, audit_emp_name, audit_date, state, bus_type_code, bus_type_name, state_name, note, add_manage_depre, add_depre_month, now_depre, now_manage_depre, ass_card_no, ass_id, ass_no, ass_code, ass_name from sum_tmp
order by sell_out_no desc
