mapper.insert:
INSERT INTO ASS_SHARE_DEPT_R_SPECIAL ( group_id , hos_id , copy_code , ass_card_no , dept_id , dept_no , area , note , ass_year , ass_month ) select t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.ass_card_no} , to_number(#{item.dept_id}) , to_number(#{item.dept_no}) , to_number(#{item.area}) , #{item.note}, null note, #{item.ass_year} , #{item.ass_month} from dual
)t

mapper.update:

UPDATE ASS_SHARE_DEPT_R_SPECIAL 


area = #{area}
,


note = #{note}
,




AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND ass_card_no = #{ass_card_no}


AND dept_id = #{dept_id}


AND dept_no = #{dept_no}


AND ass_year = #{ass_year}


AND ass_month = #{ass_month}

update.foreach:
UPDATE ASS_SHARE_DEPT_R_SPECIAL 


area = #{item.area}
,


note = #{item.note}
,




AND group_id = #{item.group_id}


AND hos_id = #{item.hos_id}


AND copy_code = #{item.copy_code}


AND ass_card_no = #{item.ass_card_no}


AND dept_id = #{item.dept_id}


AND dept_no = #{item.dept_no}


AND ass_year = #{item.ass_year}


AND ass_month = #{item.ass_month}

mapper.delete:

DELETE FROM ASS_SHARE_DEPT_R_SPECIAL 

AND group_id = #{group_id}
AND hos_id = #{hos_id}
AND copy_code = #{copy_code}
AND ass_card_no = #{ass_card_no}

AND ass_year = #{ass_year}


AND ass_month = #{ass_month}

