		AND dept_no = #{item.dept_no}


		AND ass_year = #{item.ass_year}


		AND ass_month = #{item.ass_month}

DELETE FROM ASS_SHARE_DEPT_R_SPECIAL 


		group_id = #{item.group_id}
		AND hos_id = #{item.hos_id}
		AND copy_code = #{item.copy_code}
		AND ass_card_no = #{item.ass_card_no}
		AND ass_year = #{item.ass_year}
		AND ass_month = #{item.ass_month}

mapper.select:

SELECT 
	a.group_id,
	a.hos_id,
	a.copy_code,
	a.ass_card_no,
	a.dept_id||'@'||a.dept_no dept_id,
	a.dept_no,
	a.ass_year,
	a.ass_month,
	hdd.dept_code,
	hdd.dept_code||' '||hdd.dept_name dept_name,
	a.area,
	a.note
FROM ASS_SHARE_DEPT_R_SPECIAL  a
left join hos_dept_dict hdd on 
a.group_id = hdd.group_id
and a.hos_id = hdd.hos_id
and a.dept_id = hdd.dept_id
and hdd.is_stop = 0


AND a.group_id = #{group_id}


AND a.hos_id = #{hos_id}


AND a.copy_code = #{copy_code}


AND a.ass_card_no = #{ass_card_no}


AND a.dept_id = #{dept_id}


AND a.dept_no = #{dept_no}


AND a.ass_year = #{ass_year}


AND a.ass_month = #{ass_month}


AND a.area = #{area}


AND a.note = #{note}


order by a.dept_id asc


SELECT 
group_id,
hos_id,
copy_code,
ass_card_no,
dept_id,
dept_no,
area,
note
FROM ASS_SHARE_DEPT_R_SPECIAL 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND ass_card_no = #{ass_card_no}


AND dept_id = #{dept_id}


AND dept_no = #{dept_no}


AND area = #{area}


AND note = #{note}


order by group_id asc


SELECT 
group_id,
hos_id,
copy_code,
ass_card_no,
dept_id,
dept_no,
area,
note
FROM ASS_SHARE_DEPT_R_SPECIAL 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND ass_card_no = #{ass_card_no}


AND dept_id = #{dept_id}


AND dept_no = #{dept_no}


AND area = #{area}


AND note = #{note}


order by group_id asc
