INSERT INTO ASS_TRAN_DEPT_DETAIL_Other ( group_id , hos_id , copy_code , tran_no , ass_card_no , note
) select t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.tran_no} , #{item.ass_card_no} ,
#{item.note}
null note
from dual
)t

mapper.update:

UPDATE ass_tran_dept_detail_Other 


note = #{note}
,




AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND tran_no = #{tran_no}


AND ass_card_no = #{ass_card_no}

update.foreach:
UPDATE ass_tran_dept_detail_Other 


note = #{item.note}
,




AND group_id = #{item.group_id}


AND hos_id = #{item.hos_id}


AND copy_code = #{item.copy_code}


AND tran_no = #{item.tran_no}


AND ass_card_no = #{item.ass_card_no}

