mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.tran_no, a.ass_card_no, c.ass_id, c.ass_no, c.ass_code, c.ass_name, a.note, b.ass_spec, b.ass_mondl ass_model, b.ass_brand, b.price, b.ass_seq_no, hu.unit_name ass_unit_name FROM ASS_TRAN_DEPT_DETAIL_Other a left join ass_card_Other b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_card_no = b.ass_card_no
left join hos_unit hu on b.group_id = hu.group_id and b.hos_id = hu.hos_id and b.unit_code = hu.unit_code
left join ass_no_dict c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_id = c.ass_id and c.is_stop = 0
AND a.group_id = #{group_id}
AND a.hos_id = #{hos_id}
AND a.copy_code = #{copy_code}
AND a.tran_no = #{tran_no}
AND a.ass_card_no = #{ass_card_no}
AND a.note = #{note}
order by a.tran_no,a.ass_card_no asc

SELECT a.group_id, a.hos_id, a.copy_code, a.tran_no, a.ass_card_no, c.ass_id, c.ass_no, c.ass_code, c.ass_name, a.note, d.out_dept_id, d.out_dept_no, d.in_dept_id, d.in_dept_no FROM ASS_TRAN_DEPT_DETAIL_Other a left join ass_card_Other b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_card_no = b.ass_card_no
left join ass_no_dict c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_id = c.ass_id and c.is_stop = 0
left join ASS_TRAN_DEPT_Other d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.copy_code = d.copy_code and a.tran_no = d.tran_no
AND a.group_id = #{group_id} AND a.hos_id = #{hos_id} AND a.copy_code = #{copy_code} AND a.tran_no = #{tran_no}
and b.ass_card_no = #{ass_card_no}


SELECT 
group_id,
hos_id,
copy_code,
tran_no,
ass_card_no,
note
FROM ASS_TRAN_DEPT_DETAIL_Other 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND tran_no = #{tran_no}


AND ass_card_no = #{ass_card_no}


AND note = #{note}


order by group_id asc


