mapper.insert:
INSERT INTO ASS_TRAN_DEPT_Other ( group_id , hos_id , copy_code , tran_no , out_dept_id , out_dept_no , in_dept_id , in_dept_no , create_emp , create_date , state , note
) select t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.tran_no} , #{item.out_dept_id} , #{item.out_dept_no} , #{item.in_dept_id} , #{item.in_dept_no} , #{item.create_emp} , #{item.create_date} , #{item.state} , #{item.note}
from dual
)t

mapper.update:

UPDATE ass_tran_dept_Other 


out_dept_id = #{out_dept_id}
,


out_dept_no = #{out_dept_no}
,


in_dept_id = #{in_dept_id}
,


in_dept_no = #{in_dept_no}
,


create_date = to_date(#{create_date},'YYYY-MM-DD')
,


note = #{note}
,


note = null
,




AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND tran_no = #{tran_no}

