mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.tran_no, a.out_dept_id, a.out_dept_no, d1.dept_code out_dept_code, d1.dept_name out_dept_name, a.in_dept_id, a.in_dept_no, d2.dept_code in_dept_code, d2.dept_name in_dept_name, a.create_emp, su1.user_name create_emp_name, a.create_date, a.audit_emp, su2.user_name audit_emp_name, a.audit_date, a.state, case when a.state = 0 then '新建' when a.state = 1 then '审核' when a.state = 2 then '移库确认' else '' end state_name, a.note FROM ass_tran_dept_Other a left join hos_dept_dict d1 on a.group_id = d1.group_id and a.hos_id = d1.hos_id and a.out_dept_id = d1.dept_id and d1.is_stop = 0
left join hos_dept_dict d2 on a.group_id = d2.group_id and a.hos_id = d2.hos_id and a.in_dept_id = d2.dept_id and d2.is_stop = 0
left join sys_user su1 on a.group_id = su1.group_id and a.hos_id = su1.hos_id and a.create_emp = su1.user_id
left join sys_user su2 on a.group_id = su2.group_id and a.hos_id = su2.hos_id and a.audit_emp = su2.user_id
AND a.group_id = #{group_id} AND a.hos_id = #{hos_id} AND a.copy_code = #{copy_code} and exists ( select 1 from v_user_data_perm b where a.group_id=b.group_id and a.hos_id=b.hos_id and to_char(a.out_dept_id) = b.perm_code and user_id=#{user_id} AND TABLE_CODE = upper('hos_dept_dict') and b.is_write = 1
union all
select 1 from v_user_data_perm b where a.group_id=b.group_id and a.hos_id=b.hos_id and to_char(a.out_dept_id) = b.perm_code and user_id=#{user_id} AND TABLE_CODE = upper('hos_dept_dict') and b.is_read = 1 )
AND a.tran_no = #{tran_no}
AND a.out_dept_id = #{out_dept_id}
AND a.in_dept_id = #{in_dept_id}
AND a.create_emp = #{create_emp}
AND a.create_date between to_date(#{create_date_beg},'YYYY-MM-DD') and to_date(#{create_date_end},'YYYY-MM-DD')
AND a.audit_emp = #{audit_emp}
AND a.audit_date between to_date(#{audit_date_beg},'YYYY-MM-DD') and to_date(#{audit_date_end},'YYYY-MM-DD')
AND a.state = #{state}
AND a.note = #{note}
and exists (select 1 from ASS_TRAN_DEPT_DETAIL_Other b left join ass_card_Other c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_card_no = c.ass_card_no
left join ass_no_dict d on c.group_id = d.group_id and c.hos_id = d.hos_id and c.copy_code = d.copy_code and c.ass_id = d.ass_id and d.is_stop = 0 where a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.tran_no = b.tran_no and ( d.ass_code like '%${ass_name}%' or d.ass_name like '%${ass_name}%' or d.spell_code like '%${ass_name}%' or d.wbx_code like '%${ass_name}%' or c.ass_spec like '%${ass_name}%' )
)
order by a.create_date desc


SELECT 
group_id,
hos_id,
copy_code,
tran_no,
out_dept_id,
out_dept_no,
in_dept_id,
in_dept_no,
create_emp,
create_date,
audit_emp,
audit_date,
state,
note
FROM ASS_TRAN_DEPT_Other 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND tran_no = #{tran_no}


AND out_dept_id = #{out_dept_id}


AND out_dept_no = #{out_dept_no}


AND in_dept_id = #{in_dept_id}


AND in_dept_no = #{in_dept_no}


AND create_emp = #{create_emp}


AND create_date = #{create_date}


AND audit_emp = #{audit_emp}


AND audit_date = #{audit_date}


AND state = #{state}


AND note = #{note}


order by create_date desc


SELECT 
