SELECT
  tran_no
FROM ASS_TRAN_DEPT_Other 
WHERE                     
group_id = #{group_id}

AND hos_id = #{hos_id}

AND copy_code = #{copy_code}


AND tran_no in (${paraId})


AND tran_no = #{tran_no}

AND  state != 2

SELECT a.tran_no ID, a.ass_card_no, b.ass_brand, b.ass_spec, b.ass_mondl, b.fac_no, b.fac_id, hfd.fac_code, hfd.fac_name, b.ass_amount, h.unit_name, to_char(b.price,'FM999,999,990.00') as price, to_char(b.ass_amount*b.price,'FM999,999,990.00') price_sum, to_char(b.price,'FM999,999,990.00') price_detail, to_char(b.depre_money,'FM999,999,990.00') add_depre_detail, b.add_depre_month, to_char(b.cur_money,'FM999,999,990.00') cur_money_detail, to_char(b.fore_money,'FM999,999,990.00') fore_money_detail, c.ass_id, c.ass_no, c.ass_code, c.ass_name, a.note note_detail FROM ASS_TRAN_DEPT_DETAIL_Other a left join ass_card_other b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_card_no = b.ass_card_no left join hos_fac_dict hfd on b.group_id = hfd.group_id and b.hos_id = hfd.hos_id and b.fac_no = hfd.fac_no and b.fac_id = hfd.fac_id left join ass_no_dict c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_id = c.ass_id and c.is_stop = 0 left join HOS_UNIT h on h.group_id = b.group_id and h.hos_id = b.hos_id and h.unit_code=b.unit_code WHERE a.group_id = #{group_id}
AND a.hos_id = #{hos_id}
AND a.copy_code = #{copy_code}
and a.tran_no in (${paraId})
and a.tran_no = #{tran_no,jdbcType=NUMERIC}
order by a.ass_card_no desc

with detail_tmp as (select a.group_id, a.hos_id, a.copy_code, a.tran_no, a.out_dept_id, a.out_dept_no, d1.dept_code out_dept_code, d1.dept_name out_dept_name, a.in_dept_id, a.in_dept_no, d2.dept_code in_dept_code, d2.dept_name in_dept_name, a.create_emp, su1.user_name create_emp_name, a.create_date, a.audit_emp, su2.user_name audit_emp_name, a.audit_date, a.state, case when a.state = 0 then '新建' when a.state = 1 then '审核' when a.state = 2 then '移库确认' else '' end state_name, a.note, b.ass_card_no, d.ass_id, d.ass_no, d.ass_code, d.ass_name, c.ass_spec, c.ass_mondl, c.ass_brand, c.ass_amount, c.price from ass_tran_dept_Other a left join ASS_TRAN_DEPT_DETAIL_Other b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.tran_no = b.tran_no left join hos_dept_dict d1 on a.group_id = d1.group_id and a.hos_id = d1.hos_id and a.out_dept_id = d1.dept_id and d1.is_stop = 0
left join hos_dept_dict d2 on a.group_id = d2.group_id and a.hos_id = d2.hos_id and a.in_dept_id = d2.dept_id and d2.is_stop = 0
left join sys_user su1 on a.group_id = su1.group_id and a.hos_id = su1.hos_id and a.create_emp = su1.user_id
left join sys_user su2 on a.group_id = su2.group_id and a.hos_id = su2.hos_id and a.audit_emp = su2.user_id left join ass_card_Other c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_card_no = c.ass_card_no
left join ass_no_dict d on c.group_id = d.group_id and c.hos_id = d.hos_id and c.copy_code = d.copy_code and c.ass_id = d.ass_id and d.is_stop = 0
AND a.group_id = #{group_id} AND a.hos_id = #{hos_id} AND a.copy_code = #{copy_code} and exists ( select 1 from v_user_data_perm b where a.group_id=b.group_id and a.hos_id=b.hos_id and to_char(a.out_dept_id) = b.perm_code and user_id=#{user_id} AND TABLE_CODE = upper('hos_dept_dict') and b.is_write = 1
union all
select 1 from v_user_data_perm b where a.group_id=b.group_id and a.hos_id=b.hos_id and to_char(a.out_dept_id) = b.perm_code and user_id=#{user_id} AND TABLE_CODE = upper('hos_dept_dict') and b.is_read = 1 )
AND a.tran_no = #{tran_no}
AND a.out_dept_id = #{out_dept_id}
AND a.in_dept_id = #{in_dept_id}
AND a.create_emp = #{create_emp}
AND a.create_date between to_date(#{create_date_beg},'YYYY-MM-DD') and to_date(#{create_date_end},'YYYY-MM-DD')
AND a.audit_emp = #{audit_emp}
AND a.audit_date between to_date(#{audit_date_beg},'YYYY-MM-DD') and to_date(#{audit_date_end},'YYYY-MM-DD')
AND a.state = #{state}
AND a.note = #{note}
and ( d.ass_code like '%${ass_name}%' or d.ass_name like '%${ass_name}%' or d.spell_code like '%${ass_name}%' or d.wbx_code like '%${ass_name}%' or c.ass_spec like '%${ass_name}%' )
), sum_tmp as (select null group_id, null hos_id, null copy_code, null tran_no, null out_dept_id, null out_dept_no, null out_dept_code, null out_dept_name, null in_dept_id, null in_dept_no, null in_dept_code, '合计' in_dept_name, null create_emp, null create_emp_name, null create_date, null audit_emp, null audit_emp_name, null audit_date, null state, null state_name, null note, null ass_card_no, null ass_id, null ass_no, null ass_code, null ass_name, null ass_spec, null ass_mondl, null ass_brand, null ass_amount, sum(price) price from detail_tmp)
select group_id, hos_id, copy_code, tran_no, out_dept_id, out_dept_no, out_dept_code, out_dept_name, in_dept_id, in_dept_no, in_dept_code, in_dept_name, create_emp, create_emp_name, create_date, audit_emp, audit_emp_name, audit_date, state, state_name, note, ass_card_no, ass_id, ass_no, ass_code, ass_name, ass_spec, ass_mondl, ass_brand, ass_amount, price from detail_tmp union all select group_id, hos_id, copy_code, tran_no, out_dept_id, out_dept_no, out_dept_code, out_dept_name, in_dept_id, in_dept_no, in_dept_code, in_dept_name, create_emp, create_emp_name, create_date, audit_emp, audit_emp_name, audit_date, state, state_name, note, ass_card_no, ass_id, ass_no, ass_code, ass_name, ass_spec, ass_mondl, ass_brand, ass_amount, price from sum_tmp
order by create_date desc
