INSERT INTO ASS_OUT_DETAIL_INASSETS ( group_id , hos_id , copy_code , out_no , ass_card_no , note
) select t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.out_no} , #{item.ass_card_no} ,
#{item.note}
null note
from dual
)t

mapper.update:

UPDATE ass_out_detail_inassets 


note = #{note}
,




AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND out_no = #{out_no}


AND ass_card_no = #{ass_card_no}

update.foreach:
UPDATE ass_out_detail_inassets 


note = #{item.note}
,




AND group_id = #{item.group_id}


AND hos_id = #{item.hos_id}


AND copy_code = #{item.copy_code}


AND out_no = #{item.out_no}


AND ass_card_no = #{item.ass_card_no}

mapper.delete:

