mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.out_no, a.ass_card_no, c.ass_id, c.ass_no, c.ass_code, c.ass_name, a.note FROM ASS_OUT_DETAIL_INASSETS a left join ass_card_inassets b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_card_no = b.ass_card_no
left join ass_no_dict c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_id = c.ass_id and c.is_stop = 0
AND a.group_id = #{group_id}
AND a.hos_id = #{hos_id}
AND a.copy_code = #{copy_code}
AND a.out_no = #{out_no}
AND a.ass_card_no = #{ass_card_no}
AND a.note = #{note}
order by a.out_no,a.ass_card_no asc

SELECT a.group_id, a.hos_id, a.copy_code, a.out_no, a.ass_card_no, c.ass_id, c.ass_no, c.ass_code, c.ass_name, a.note, d.bill_type, d.store_id, d.store_no, d.dept_id, d.dept_no FROM ASS_OUT_DETAIL_INASSETS a left join ass_card_inassets b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_card_no = b.ass_card_no
left join ass_no_dict c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_id = c.ass_id and c.is_stop = 0
left join ASS_OUT_INASSETS d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.copy_code = d.copy_code and a.out_no = d.out_no
AND a.group_id = #{group_id} AND a.hos_id = #{hos_id} AND a.copy_code = #{copy_code} AND a.out_no = #{out_no}
and b.ass_card_no = #{ass_card_no}


SELECT 
group_id,
hos_id,
copy_code,
out_no,
ass_card_no,
note
FROM ASS_OUT_DETAIL_INASSETS 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND out_no = #{out_no}


AND ass_card_no = #{ass_card_no}


AND note = #{note}


order by group_id asc


