mapper.insert:
INSERT INTO ass_in_out_map_Other ( group_id , hos_id , copy_code , ass_in_no , out_no , bill_type
) select t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.ass_in_no} , #{item.out_no} , #{item.bill_type}
from dual
)t

mapper.update:

UPDATE ass_in_out_map_Other 




AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND ass_in_no = #{ass_in_no}


AND out_no = #{out_no}


AND bill_type = #{bill_type}

update.foreach:
