mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.budg_year, a.proj_id, f.proj_code , f.proj_name , a.source_id , d.source_name , a.budg_amount , b.subj_code , c.subj_name
FROM BUDG_PROJ_YEAR a
left join budg_proj_income_subj b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.budg_year = b.budg_year and a.proj_id = b.proj_id left join budg_income_subj c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.budg_year = c.budg_year and b.subj_code = c.subj_code left join hos_source d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.source_id = d.source_id and d.is_stop = 0 left join hos_proj_dict f on a.group_id = f.group_id and a.hos_id = f.hos_id and a.proj_id = f.proj_id and f.is_stop = 0 where a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.budg_year = #{budg_year,jdbcType=VARCHAR}
AND a.proj_id = #{proj_id,jdbcType=INTEGER}
AND a.source_id = #{source_id,jdbcType=INTEGER}
and d.source_attr = 4

SELECT a.group_id, a.hos_id, a.copy_code, a.budg_year, a.proj_id, f.proj_code , f.proj_name , a.source_id , d.source_name , a.budg_amount , a.in_amount , b.subj_code , c.subj_name
FROM BUDG_PROJ_YEAR a
left join budg_proj_income_subj b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.budg_year = b.budg_year and a.proj_id = b.proj_id left join budg_income_subj c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.budg_year = c.budg_year and b.subj_code = c.subj_code left join hos_source d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.source_id = d.source_id and d.is_stop = 0 left join hos_proj_dict f on a.group_id = f.group_id and a.hos_id = f.hos_id and a.proj_id = f.proj_id and f.is_stop = 0 where a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.budg_year = #{budg_year,jdbcType=VARCHAR}
AND a.proj_id = #{proj_id,jdbcType=INTEGER}
AND a.source_id = #{source_id,jdbcType=INTEGER}
and d.source_attr = 4
