mapper.select:
SELECT ban.naturs_code, ban.group_id, ban.hos_id, ban.copy_code, ban.budg_year, ban.dept_id, hd.dept_code, hd.dept_name, ban.month, ban.ass_card_no, ad.ass_code, ad.ass_name, ban.source_id, hs.source_code, hs.source_name, ban.depr_count, ban.depr_budg FROM BUDG_ASSET_NOW ban left join HOS_DEPT hd on ban.dept_id = hd.dept_id and ban.group_id = hd.group_id and ban.hos_id = hd.hos_id and hd.is_stop = 0 left join HOS_SOURCE hs on ban.source_id = hs.source_id and ban.group_id = hs.group_id and ban.hos_id = hs.hos_id and hs.is_stop = 0 left join ass_naturs an on ban.naturs_code = an.naturs_code left join BUDG_ASS_CARD bac on ban.ass_card_no = bac.ass_card_no and ban.naturs_code = bac.naturs_code and ban.group_id = bac.group_id and ban.hos_id = bac.hos_id and ban.copy_code = bac.copy_code left join ASS_DICT ad on bac.ass_id = ad.ass_id and ban.group_id = ad.group_id and ban.hos_id = ad.hos_id and ban.copy_code = ad.copy_code left join ASS_TYPE_DICT atd on ad.ass_type_id = atd.ass_type_id and ban.group_id = atd.group_id and ban.hos_id = atd.hos_id and ban.copy_code = atd.copy_code
AND ban.group_id = #{group_id,jdbcType=INTEGER} AND ban.hos_id = #{hos_id,jdbcType=INTEGER} AND ban.copy_code = #{copy_code,jdbcType=VARCHAR}
AND ban.naturs_code = #{ass_natures,jdbcType=INTEGER}
AND ban.budg_year = #{budg_year,jdbcType=VARCHAR}
AND ban.month = #{month,jdbcType=VARCHAR}
AND ban.dept_id = #{dept_id,jdbcType=INTEGER}
AND atd.ass_type_id = #{ass_type,jdbcType=INTEGER}
AND ad.ass_id = #{ass_id,jdbcType=INTEGER}
AND ban.source_id = #{source_id,jdbcType=INTEGER}
AND ban.ass_card_no LIKE #{pattern}
order by ban.group_id asc ,ban.month,ban.ass_card_no

SELECT bac.naturs_code, bac.group_id, bac.hos_id, bac.copy_code, bac.ass_card_no, bac.ass_id, bac.dept_id, bac.is_depr, bac.use_state, bac.add_depre_month, bac.last_depr_year, bac.last_depr_month, bac.acc_depre_amount, bac.depr_method, nvl(bacs.price,0) price, nvl(bacs.fore_money,0) fore_money, nvl(bacs.depre_money,0) depre_money, bacs.source_id FROM BUDG_ASS_CARD bac left join budg_ass_card_source bacs on bac.ass_card_no = bacs.ass_card_no and bac.naturs_code = bacs.naturs_code and bac.group_id = bacs.group_id and bac.hos_id = bacs.hos_id and bac.copy_code = bacs.copy_code
AND bac.group_id = #{group_id,jdbcType=INTEGER}
AND bac.hos_id = #{hos_id,jdbcType=INTEGER}
AND bac.copy_code = #{copy_code,jdbcType=VARCHAR}


SELECT 
group_id,
hos_id,
copy_code,
year,
dept_id,
month,
subj_code,
amount,
remark
FROM BUDG_ASSET_NOW 


AND group_id = #{group_id,jdbcType=INTEGER}


AND hos_id = #{hos_id,jdbcType=INTEGER}


AND copy_code = #{copy_code,jdbcType=VARCHAR}


AND year = #{year,jdbcType=VARCHAR}


AND dept_id = #{dept_id,jdbcType=INTEGER}


AND month = #{month,jdbcType=VARCHAR}


AND subj_code = #{subj_code,jdbcType=VARCHAR}


AND amount = #{amount,jdbcType=INTEGER}


AND remark = #{remark,jdbcType=VARCHAR}


order by group_id asc


