SELECT 
    group_id,
    hos_id,
    copy_code,
    asset_type_code,
    asset_type_name,
    supper_code,
    is_last,
    spell_code,
    wbx_code
FROM COST_IASSET_TYPE_ARRT 


            AND group_id = #{group_id}


            AND hos_id = #{hos_id}


            AND copy_code = #{copy_code}


            AND asset_type_code like '%${asset_type_code}%'


            AND asset_type_name like '%${asset_type_name}%'


            AND supper_code = #{supper_code}


            AND is_last = #{is_last}


            AND spell_code = #{spell_code}


            AND wbx_code = #{wbx_code}


SELECT 
    group_id,
    hos_id,
    copy_code,
    asset_type_code,
    asset_type_name,
    supper_code,
    is_last,
    spell_code,
    wbx_code
FROM COST_IASSET_TYPE_ARRT 


            AND group_id = #{group_id}


            AND hos_id = #{hos_id}


            AND copy_code = #{copy_code}


            AND asset_type_code like '%${asset_type_code}%'


            AND asset_type_name like '%${asset_type_name}%'


            AND supper_code = #{supper_code}


            AND is_last = #{is_last}


            AND spell_code = #{spell_code}


            AND wbx_code = #{wbx_code}

mapper.insert:
insert into COST_IASSET_TYPE_ARRT ( group_id, hos_id, copy_code, asset_type_code, asset_type_name, supper_code, is_last, spell_code, wbx_code )
SELECT group_id, hos_id, copy_code, ass_type_code, ass_type_name, super_code, is_last, spell_code, wbx_code FROM ( SELECT group_id, hos_id, copy_code, ass_type_code, ass_type_name, case when super_code is null then 'TOP'else super_code end super_code, is_last, spell_code, wbx_code FROM ASS_TYPE_DICT a where not exists (select 1 from COST_IASSET_TYPE_ARRT b where a.group_id=b.group_id and a.hos_id=b.hos_id and a.copy_code=b.copy_code and a.ass_type_code=b.asset_type_code ) and a.group_id = #{group_id} and a.hos_id= #{hos_id} and a.copy_code =#{copy_code} and a.ASS_NATURS in('05','06')
and a.ass_type_code =#{ass_type_code}
)t
