mapper.select:
with temp as
 (select type_code, type_name, sum(t_3) t
    from cost_c0201
   group_id = #{group_id}
	 and hos_id = #{hos_id}
	 and copy_code = #{copy_code}

	and acc_year || acc_month between #{year_month_begin} and #{year_month_end}


   group by type_code, type_name
  union all
  select cost_type_code, cost_type_name, null
    from cost_type_dict a
   where not exists
   (select 1 from cost_c0201 b where a.cost_type_code = b.type_code 
      and group_id = #{group_id}
      and hos_id = #{hos_id}
      and copy_code = #{copy_code}
      and acc_year || acc_month between #{year_month_begin}  and #{year_month_end}
     )),t as(
 select type_code, type_name, t,(t) / sum(t) over() * 100 as t_ratio
  from temp
)select type_code cost_type_code ,type_name cost_type_name,t,t_ratio from t
where exists (
	select sum(t)
	from t
	where t.type_code = t.type_code
	having sum(t) != 0
)
union all
select null,'合计',sum(t) t,sum(t_ratio) t_ratio from t
order by cost_type_code

		 with temp as 
(
		select group_id, hos_id, copy_code, acc_year, acc_month, type_code, type_name,
		  t_1,t_2,t_3, t_4, t_5, t_6, t_7, t_8 from cost_c0201 


                     group_id = #{group_id,jdbcType=INTEGER}


                    AND hos_id = #{hos_id,jdbcType=INTEGER}


                    AND copy_code = #{copy_code,jdbcType=VARCHAR}


                    AND acc_year = #{acc_year,jdbcType=VARCHAR}


                    AND acc_month = #{acc_month,jdbcType=VARCHAR}


				AND acc_year || acc_month between #{year_month_begin} and #{year_month_end}


                    AND type_code = #{type_code,jdbcType=VARCHAR}


                    AND type_name = #{type_name,jdbcType=VARCHAR}


                    AND t_1 = #{t_1,jdbcType=INTEGER}


                    AND t_2 = #{t_2,jdbcType=INTEGER}


                    AND t_3 = #{t_3,jdbcType=INTEGER}


                    AND t_4 = #{t_4,jdbcType=INTEGER}


                    AND t_5 = #{t_5,jdbcType=INTEGER}


                    AND t_6 = #{t_6,jdbcType=INTEGER}


                    AND t_7 = #{t_7,jdbcType=INTEGER}


                    AND t_8 = #{t_8,jdbcType=INTEGER}


		)select group_id,hos_id,copy_code,acc_year,
				type_code,type_name, sum(t_1) t_1 , sum(t_2) t_2,sum(t_3) t_3 , 
				sum(t_4) t_4, sum(t_5) t_5, sum(t_6) t_6,sum(t_7) t_7,sum(t_8) t_8 from temp
				group by group_id,hos_id,copy_code,acc_year,
				type_code,type_name
   union all
   select  null,null, null,null,null ,'总计', sum(t_1) , sum(t_2),sum(t_3) , sum(t_4), sum(t_5), sum(t_6),sum(t_7),sum(t_8) from temp
    order by type_code

		 with temp as 
(
		select group_id, hos_id, copy_code, acc_year, acc_month, type_code, type_name,
		  t_1,t_2,t_3, t_4, t_5, t_6, t_7, t_8 from cost_c0201 


                     group_id = #{group_id,jdbcType=INTEGER}


