   select  null,null, null,null,null , '总计', sum(t_1) , sum(t_2),sum(t_3) , sum(t_4), sum(t_5), sum(t_6),sum(t_7),sum(t_8),sum(t_9) t_9,sum(t_10) t_10 from temp
