				dept_code,dept_name, sum(t_1) t_1, sum(t_2) t_2, sum(t_3) t_3 from temp
				group by  group_id,hos_id,copy_code,acc_year,
				dept_code,dept_name
   union all
   select  null,null, null,null,null , '总计', sum(t_1), sum(t_2), sum(t_3)  from temp
    order by dept_code

		 with temp as 
(
		select flag,group_id, acc_year, acc_month, item_code, 
		item_name, t_1, t_2 from cost_c0228


                     group_id = #{group_id,jdbcType=INTEGER}


                    AND acc_year = #{acc_year,jdbcType=VARCHAR}


                    AND acc_month = #{acc_month,jdbcType=VARCHAR}


				AND acc_year || acc_month between #{year_month_begin} and #{year_month_end}


		)select group_id,acc_year,
				item_code,item_name, sum(t_1) t_1, sum(t_2) t_2,flag from temp
				group by group_id,acc_year,
				item_code,item_name,flag
   union all
   select  null, null,null , '总计', sum(t_1), sum(t_2),null  from temp
   where item_code='00'
   order by flag,item_code

