		left join hos_info t2 on t1.group_id=t2.group_id and t1.hos_id=t2.hos_id


                     t1.group_id = #{group_id,jdbcType=INTEGER}


                    AND t1.hos_id = #{hos_id,jdbcType=INTEGER}


