mapper.insert:
INSERT INTO ASS_EQ_USE_RECORDM ( group_id , hos_id , copy_code , ur_rowid , analysis_code , use_date , start_time , end_date , end_time , work_load_num , unit_code , dept_code , patient_id , patient_sex , patient_age , patient_name , price , total_fee , alone_pay_num , year , month , charge_kind_id , busi_data_source_code , ex_id , is_input_flag , status , invalid_flag , remark , doctor_order_id , operator , positive_flag , sample_no , exposure_num , start_date , other_info , add_user , add_date , add_time
) select t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.ur_rowid} , #{item.analysis_code} , #{item.use_date} , #{item.start_time} , #{item.end_date} , #{item.end_time} , #{item.work_load_num} , #{item.unit_code} , #{item.dept_code} , #{item.patient_id} , #{item.patient_sex} , #{item.patient_age} , #{item.patient_name} , #{item.price} , #{item.total_fee} , #{item.alone_pay_num} , #{item.year} , #{item.month} , #{item.charge_kind_id} , #{item.busi_data_source_code} , #{item.ex_id} , #{item.is_input_flag} , #{item.status} , #{item.invalid_flag} , #{item.remark} , #{item.doctor_order_id} , #{item.operator} , #{item.positive_flag} , #{item.sample_no} , #{item.exposure_num} , #{item.start_date} , #{item.other_info} , #{item.add_user} , #{item.add_date} , #{item.add_time}
from dual
)t

