mapper.insert:
INSERT INTO aphi_TARGET_METHOD ( group_id , hos_id , copy_code , target_code , fun_para_code , method_code , formula_code , fun_code
) select aphi_TARGET_METHOD_SEQ.nextval , t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.target_code} , #{item.fun_para_code} , #{item.method_code} , #{item.formula_code} , #{item.fun_code}
)t

mapper.update:
UPDATE aphi_target_method SET
method_code = #{method_code}
,
formula_code =
#{formula_code}
,
fun_code = #{fun_code}

WHERE
group_id = #{group_id}
and
hos_id = #{hos_id}
and
copy_code = #{copy_code}
and
target_code = #{target_code}
and
fun_para_code = #{fun_para_code}

update.foreach:
UPDATE aphi_target_method SET
method_code =
#{method_code}
,
formula_code = #{formula_code}
,
fun_code = #{fun_code}

WHERE
group_id = #{item.group_id}
and
hos_id = #{item.hos_id}
and
copy_code = #{item.copy_code}
and
target_code = #{item.target_code}
and
fun_para_code = #{item.fun_para_code}

mapper.delete:
DELETE FROM aphi_target_method WHERE

	group_id = #{item.group_id}
	and
	hos_id = #{item.hos_id}
	and
	copy_code = #{item.copy_code}
	and
	target_code = #{item.target_code}

mapper.select:
SELECT
group_id,
hos_id,
copy_code,
target_code,
fun_para_code,
method_code,
formula_code,
fun_code
FROM aphi_TARGET_METHOD


		AND group_id = #{group_id}


		AND hos_id = #{hos_id}


		AND copy_code = #{copy_code}


		AND target_code = #{target_code}


		AND fun_para_code = #{fun_para_code}


		AND method_code = #{method_code}


		AND formula_code = #{formula_code}


		AND fun_code = #{fun_code}


order by target_code asc

SELECT ptm.group_id, ptm.hos_id, ptm.copy_code, ptm.target_code , pt.target_name , pt.target_note, ptm.method_code , ptmp.method_name, ptm.formula_code , pfo.formula_name , ptm.fun_code , pfu.fun_name , ptn.nature_code , ptn.nature_name , to_char(pfo.formula_method_chs) formula_method_chs, pfo.formula_method_eng, pfu.fun_note , pt.is_stop, pfu.fun_method_chs FROM aphi_target_method ptm left join aphi_target_method_para ptmp on ptm.method_code = ptmp.method_code and ptm.group_id = ptmp.group_id and ptm.hos_id = ptmp.hos_id and ptm.copy_code = ptmp.copy_code left join aphi_target pt on ptm.target_code= pt.target_code and ptm.group_id= pt.group_id and ptm.hos_id = pt.hos_id and ptm.copy_code = pt.copy_code left join aphi_target_nature ptn on pt.target_nature = ptn.nature_code and ptm.group_id = ptn.group_id and ptm.hos_id = ptn.hos_id and ptm.copy_code = ptn.copy_code left join aphi_formula pfo on ptm.formula_code = pfo.formula_code and ptm.group_id = pfo.group_id and ptm.hos_id = pfo.hos_id and ptm.copy_code = pfo.copy_code left join aphi_fun pfu on ptm.fun_code = pfu.fun_code and ptm.group_id = pfu.group_id and ptm.hos_id = pfu.hos_id and ptm.copy_code = pfu.copy_code
AND ptm.group_id = #{group_id}
AND ptm.hos_id = #{hos_id}
AND ptm.copy_code = #{copy_code}
AND ptm.method_code = #{method_code}
AND ptm.target_code = #{target_code}
AND ptn.nature_code = #{nature_code}
AND ptm.spell_code = #{spell_code}
AND ptm.wbx_code = #{wbx_code}
AND pt.is_stop = #{is_stop}
order by ptm.target_code asc
