mapper.insert:
INSERT INTO aphi_TARGET_METHOD_PARA ( group_id , hos_id , copy_code , method_code , method_name , spell_code , wbx_code , is_stop
) select aphi_TARGET_METHOD_PARA_SEQ.nextval , t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.method_code} , #{item.method_name} , #{item.spell_code} , #{item.wbx_code} , #{item.is_stop}
)t

mapper.update:

UPDATE aphi_target_method_para SET
method_name = #{method_name}
,
spell_code = #{spell_code}
,
wbx_code = #{wbx_code}
,
is_stop = #{is_stop}

WHERE 
group_id = #{group_id}
 and 
hos_id = #{hos_id}
 and 
copy_code = #{copy_code}
 and 
method_code = #{method_code}

update.foreach:
UPDATE aphi_target_method_para SET
method_name = #{method_name}
,
spell_code = #{spell_code}
,
wbx_code = #{wbx_code}
,
is_stop = #{is_stop}

WHERE 
group_id = #{item.group_id}
 and 
hos_id = #{item.hos_id}
 and 
copy_code = #{item.copy_code}
 and 
method_code = #{item.method_code}

mapper.delete:
DELETE FROM aphi_target_method_para WHERE

		group_id = #{item.group_id}
		 and 
		hos_id = #{item.hos_id}
		 and 
		copy_code = #{item.copy_code}
		 and 
		method_code = #{item.method_code}

mapper.select:

SELECT 
group_id,
hos_id,
copy_code,
method_code,
method_name,
spell_code,
wbx_code,
is_stop
FROM aphi_TARGET_METHOD_PARA 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND method_code = #{method_code}


AND method_name = #{method_name}


AND spell_code = #{spell_code}


AND wbx_code = #{wbx_code}


AND is_stop = #{is_stop}


order by group_id asc
