mapper.select:
with oneall as( select a.fim_type_code, b.dept_code, b.dept_name, sum(b.money) as money, a.is_last, b.type_code from mat_type a left join (select hsd.dept_code, hsd.dept_name, sum(mid.amount_money) as money, mtd.fim_type_code, ada.type_code,mtd.mat_type_code from mat_out_main mim left join mat_out_detail mid on mim.group_id = mid.group_id and mim.hos_id = mid.hos_id and mim.copy_code = mid.copy_code and mim.out_id = mid.out_id and mim.out_no = mid.out_no left join mat_inv_dict midd on mid.group_id = midd.group_id and mid.hos_id = midd.hos_id and mid.copy_code = midd.copy_code and mid.inv_id = midd.inv_id and mid.inv_no = midd.inv_no left join mat_type_dict mtd on midd.group_id = mtd.group_id and midd.hos_id = mtd.hos_id and midd.copy_code = mtd.copy_code and midd.mat_type_id = mtd.mat_type_id and midd.mat_type_no = mtd.mat_type_no left join hos_dept_dict hsd on mim.group_id = hsd.group_id and mim.hos_id = hsd.hos_id and mim.dept_id = hsd.dept_id and mim.dept_no = hsd.dept_no left join acc_dept_attr ada on hsd.group_id = ada.group_id and hsd.hos_id = ada.hos_id and hsd.dept_id = ada.dept_id where mim.state = '3' and mim.bus_type_code in (select bus_type_code from mat_bus_type where bus_type_code ='3' or bus_type_code ='21' or bus_type_code ='49' or bus_type_code ='50')
and midd.mat_type_id = #{mat_type_id,jdbcType=VARCHAR}
and midd.mat_type_no= #{mat_type_no,jdbcType=VARCHAR}
and hsd.dept_id= #{dept_id,jdbcType=VARCHAR}
and midd.is_charge= #{is_charge}
and mim.store_no= #{store_no}
and mim.bus_type_code in ${bus_type_code}
and mim.store_id = #{store_id}
and mim.store_id in (select store_id from MAT_STORE_DETAIL where set_id = #{set_id})
and to_char(mim.store_id) in ( select perm_code from v_user_data_perm where group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and table_code = 'HOS_STORE_DICT' and user_id = #{user_id,jdbcType=INTEGER} and is_read = 1 and is_write = 1 )
and mtd.mat_type_code like '%${mat_type_code}'
and to_char(mim.confirm_date, 'yyyy-MM-dd') between #{begin_date} and #{end_date} group by hsd.DEPT_CODE, hsd.DEPT_name, mtd.fim_type_code, hsd.kind_code, ada.type_code,mtd.mat_type_code) b on b.mat_type_code like (a.mat_type_code || '%') and a.fim_type_code is not null and a.is_last = 1 and a.is_stop = 0
b.money != 0
group by a.fim_type_code, b.dept_code, b.dept_name, a.is_last,b.type_code order by fim_type_code) , fim_typeall as( select fim_type_code as fim_type_code, dept_code, dept_name, is_last, type_code, money from ( select fim_type_code as fim_type_code, dept_code, dept_name, is_last, type_code , money from oneall g where g.type_code='01' union all select fim_type_code, '临床合计', null, is_last, null , sum(money) from oneall where type_code ='01' group by fim_type_code, is_last order by dept_code ) union all select fim_type_code as fim_type_code, dept_code, dept_name, is_last, type_code , money from ( select fim_type_code as fim_type_code, dept_code, dept_name, is_last, type_code , money from oneall g where g.type_code='02' union all select fim_type_code, '医技合计', null, is_last, null , sum(money) from oneall where type_code ='02' group by fim_type_code, is_last order by dept_code ) union all select fim_type_code as fim_type_code, dept_code, dept_name, is_last, type_code , money from ( select fim_type_code as fim_type_code, dept_code, dept_name, is_last, type_code , money from oneall g where g.type_code='03' union all select fim_type_code, '医辅合计', null, is_last, null , sum(money) from oneall where type_code ='03' group by fim_type_code, is_last order by dept_code ) union all select fim_type_code as fim_type_code, dept_code, dept_name, is_last, type_code , money from ( select fim_type_code as fim_type_code, dept_code, dept_name, is_last, type_code , money from oneall g where g.type_code='04' union all select fim_type_code, '管理合计', null, is_last, null , sum(money) from oneall where type_code ='04' group by fim_type_code, is_last order by dept_code ) ) select fim_type_code as fim_type_code, dept_code, dept_name, is_last, type_code, money from fim_typeall union all select fim_type_code, '总合计', null, 1 as is_last, null , sum(money) from oneall group by fim_type_code,is_last
