select group_id, hos_id, copy_code, store_id, inv_id, batch_no, batch_sn, bar_code, 
	nvl(price, 0) as price, nvl(sale_price, 0) as sale_price, nvl(in_amount, 0) as in_amount, 
	nvl(in_money, 0) as in_money, nvl(in_sale_money, 0) as in_sale_money, 
	nvl(out_amount, 0) as out_amount, nvl(out_money, 0) as out_money, 
	nvl(out_sale_money, 0) as out_sale_money, nvl(left_amount, 0) as left_amount, 
	nvl(left_money, 0) as left_money, nvl(left_sale_money, 0) as left_sale_money, 
	nvl(remove_zero_error, 0) as remove_zero_error, nvl(sale_zero_error, 0) as sale_zero_error, 
	nvl(location_id, 0) location_id
from mat_fifo_balance
where group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}
	AND store_id = #{store_id,jdbcType=INTEGER}
	AND inv_id = #{inv_id,jdbcType=INTEGER}
	AND price = #{item.price,jdbcType=FLOAT}
	AND batch_sn = #{batch_sn,jdbcType=INTEGER}
	AND batch_no = #{batch_no,jdbcType=VARCHAR}
	AND bar_code = #{bar_code,jdbcType=VARCHAR}

with list_temp as (
select #{item.group_id,jdbcType=INTEGER} as group_id, #{item.hos_id,jdbcType=INTEGER} as hos_id, #{item.copy_code,jdbcType=VARCHAR} as copy_code, #{item.store_id,jdbcType=INTEGER} as store_id, #{item.inv_id,jdbcType=INTEGER} as inv_id, #{item.price,jdbcType=FLOAT} as price, #{item.batch_no,jdbcType=INTEGER} as batch_no, #{item.batch_sn,jdbcType=INTEGER} as batch_sn, #{item.bar_code,jdbcType=INTEGER} as bar_code, #{item.location_id,jdbcType=INTEGER} as location_id from dual
) select a.group_id, a.hos_id, a.copy_code, a.store_id, a.inv_id, a.batch_no, a.batch_sn, a.bar_code, nvl(b.price, 0) as price, nvl(b.sale_price, 0) as sale_price, nvl(b.in_amount, 0) as in_amount, nvl(b.in_money, 0) as in_money, nvl(b.in_sale_money, 0) as in_sale_money, nvl(b.out_amount, 0) as out_amount, nvl(b.out_money, 0) as out_money, nvl(b.out_sale_money, 0) as out_sale_money, nvl(b.left_amount, 0) as left_amount, nvl(b.left_money, 0) as left_money, nvl(b.left_sale_money, 0) as left_sale_money, nvl(b.remove_zero_error, 0) as remove_zero_error, nvl(b.sale_zero_error, 0) as sale_zero_error, nvl(b.location_id, 0) location_id from list_temp a inner join mat_fifo_balance b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.store_id = b.store_id and a.inv_id = b.inv_id and a.price = b.price and a.batch_no = b.batch_no and a.batch_sn = b.batch_sn and a.bar_code = b.bar_code and nvl(a.location_id, 0) = nvl(b.location_id, 0) order by a.store_id, a.inv_id, a.batch_no, a.batch_sn
